CREATE OR REPLACE PROCEDURE pls_gerar_protocolo_conta ( nr_seq_conta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_prestador_w		bigint;
dt_competencia_w		timestamp;
nr_seq_prestador_solic_w	bigint;
ie_tipo_guia_w			varchar(2);
nr_seq_protocolo_w		bigint;
cd_guia_w			varchar(20);
nr_seq_competencia_w		bigint;


BEGIN
select	cd_guia,
	nr_seq_competencia,
	coalesce(nr_seq_prestador,nr_seq_prestador_exec),
	ie_tipo_guia,
	nr_seq_protocolo
into STRICT	cd_guia_w,
	nr_seq_competencia_w,
	nr_seq_prestador_solic_w,
	ie_tipo_guia_w,
	nr_seq_protocolo_w
from	pls_conta
where	nr_sequencia	= nr_seq_conta_p;

if (nr_seq_competencia_w IS NOT NULL AND nr_seq_competencia_w::text <> '') then
	select	dt_mes_competencia
	into STRICT	dt_competencia_w
	from    pls_competencia
	where	nr_sequencia	= nr_seq_competencia_w
	and	cd_estabelecimento = cd_estabelecimento_p;

end if;

if (coalesce(nr_seq_protocolo_w::text, '') = '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_protocolo_w
	from	pls_protocolo_conta
	where	trunc(dt_mes_competencia,'month')	= trunc(dt_competencia_w,'month')
	and	nr_seq_prestador	= nr_seq_prestador_solic_w
	and	ie_tipo_guia	=	ie_tipo_guia_w
	and	ie_situacao	= 'D'
	and	((ie_status	= 1)
	or (ie_status	= 2));
	if (coalesce(nr_seq_protocolo_w::text, '') = '') then

		if (coalesce(dt_competencia_w::text, '') = '') then
			dt_competencia_w	:= clock_timestamp();
		end if;

		select	nextval('pls_protocolo_conta_seq')
		into STRICT	nr_seq_protocolo_w
		;
		insert into pls_protocolo_conta(nr_sequencia, nm_usuario, dt_atualizacao,
			nm_usuario_nrec, dt_atualizacao_nrec, ie_situacao,
			ie_status, dt_mes_competencia, cd_estabelecimento,
			ie_tipo_guia, ie_apresentacao, dt_protocolo,
			nr_seq_prestador, ie_origem_protocolo)
		values (nr_seq_protocolo_w, nm_usuario_p, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), 'D',
			1, dt_competencia_w, cd_estabelecimento_p,
			ie_tipo_guia_w, 'A', dt_competencia_w,
			nr_seq_prestador_solic_w, 'D');
	end if;

	update	pls_conta
	set	nr_seq_protocolo	= nr_seq_protocolo_w,
		ie_origem_conta	= 'D'
	where	nr_sequencia		= nr_seq_conta_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
