SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_protocolo_rejeitado (nr_seq_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_protocolo_w		bigint;
nr_seq_prot_origem_w		bigint;
qt_registros_w			bigint;



BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	select	a.nr_seq_protocolo
	into STRICT	nr_seq_prot_origem_w
	from	pls_conta a
	where	a.nr_sequencia	= nr_seq_conta_p;

	select	count(1)
	into STRICT	qt_registros_w
	from	pls_conta
	where	nr_seq_protocolo = nr_seq_prot_origem_w
	and	ie_status <> 'C'
	and	nr_sequencia <> nr_seq_conta_p;

	/* OS 732477 Sestari - Não pode deixar o protocolo vazio, ou seja, com todas as contas canceladas, sendo assim o protocolo atual é rejeitado */

	if (qt_registros_w = 0) then
		update	pls_protocolo_conta
		set	ie_situacao = 'RE',
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_prot_origem_w;

		update	pls_conta
		set	dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			ie_status		= 'C'
		where	nr_sequencia		= nr_seq_conta_p;
	else
		select	max(nr_seq_protocolo)
		into STRICT	nr_seq_protocolo_w
		from	pls_conta a
		where	a.nr_seq_protocolo_origem	= nr_seq_prot_origem_w;

		if (coalesce(nr_seq_protocolo_w::text, '') = '') then
			select	nextval('pls_protocolo_conta_seq')
			into STRICT	nr_seq_protocolo_w
			;

			insert into pls_protocolo_conta(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				ie_status,
				ie_situacao,
				cd_estabelecimento,
				dt_mes_competencia,
				nr_seq_prestador,
				ie_apresentacao,
				ie_protocolo_compl,
				ie_tipo_guia,
				ie_tipo_protocolo,
				ie_origem_protocolo,
				ie_guia_fisica)
			SELECT	nr_seq_protocolo_w,
				clock_timestamp(),
				nm_usuario_p,
				ie_status,
				'RE', /* Rejeitado */
				cd_estabelecimento,
				dt_mes_competencia,
				nr_seq_prestador,
				ie_apresentacao,
				ie_protocolo_compl,
				ie_tipo_guia,
				ie_tipo_protocolo,
				ie_origem_protocolo,
				ie_guia_fisica
			from	pls_protocolo_conta a
			where	a.nr_sequencia	= nr_seq_prot_origem_w;
		end if;

		update	pls_conta
		set	nr_seq_protocolo	= nr_seq_protocolo_w,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			ie_status		= 'C',
			nr_seq_protocolo_origem	= nr_seq_prot_origem_w
		where	nr_sequencia		= nr_seq_conta_p;
	end if;

end if;
/* Não pode commit */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
