CREATE OR REPLACE PROCEDURE pls_gerar_prot_spsadt_inter (nr_seq_protocolo_ref_p bigint, nr_seq_protocolo_p INOUT bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_protocolo_w		bigint;
nr_seq_prestador_w		bigint;
dt_competencia_w		timestamp;
nr_protocolo_prestador_w	varchar(20);
dt_protocolo_w			timestamp;
dt_recebimento_w		timestamp;	
ds_retorno_w			varchar(255);
			

BEGIN		 
 
/*Verificar se existe o protocolo de spsadt internado referente a este protocolo*/
 
select	coalesce(max(nr_sequencia),0) 
into STRICT	nr_seq_protocolo_p 
from	pls_protocolo_conta 
where	nr_seq_prot_referencia	= nr_seq_protocolo_ref_p 
and	ie_status in (1, 2) 
and	ie_tipo_guia = 4;
 
/*Se n√£o existir cria-se um novo protocolo de Honorario individual*/
 
if (nr_seq_protocolo_p = 0) then 
 
	select	nr_seq_prestador, 
		dt_mes_competencia, 
		nr_protocolo_prestador, 
		dt_protocolo, 
		dt_recebimento 
	into STRICT	nr_seq_prestador_w, 
		dt_competencia_w, 
		nr_protocolo_prestador_w, 
		dt_protocolo_w, 
		dt_recebimento_w 
	from	pls_protocolo_conta 
	where	nr_sequencia = nr_seq_protocolo_ref_p;
 
	SELECT * FROM pls_gerar_protocolo(nr_seq_prestador_w, dt_competencia_w, nr_seq_protocolo_p, 4, nr_protocolo_prestador_w, null, cd_estabelecimento_p, dt_protocolo_w, nm_usuario_p, nr_seq_protocolo_ref_p, 0, null, 'S', 'A', dt_recebimento_w, ds_retorno_w) INTO STRICT nr_seq_protocolo_p, ds_retorno_w;
end if;
if (coalesce(ds_retorno_w,'X') <> 'X') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(193095,'DS_ERRO=' ||ds_retorno_w);
end if;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
