CREATE OR REPLACE PROCEDURE pls_gerar_reajuste_cobr_retro ( nr_seq_cobr_retro_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nr_seq_reajuste_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_referencia_w		timestamp;
dt_fim_w		timestamp;


BEGIN
if (dt_inicio_p > dt_fim_p) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265827,'');
	--A data inicial nÃ£o pode ser superior a data final!
end if;

dt_referencia_w	:= trunc(dt_inicio_p, 'mm');
dt_fim_w	:= trunc(dt_fim_p, 'mm');

update	pls_reajuste_cobr_retro
set	nr_seq_reajuste = nr_seq_reajuste_p,
	dt_referencia_mensalidade = dt_referencia_w
where	nr_sequencia	= nr_seq_cobr_retro_p;

dt_referencia_w	:= add_months(dt_referencia_w,1);
while dt_referencia_w <= dt_fim_w loop
	begin
	insert	into	pls_reajuste_cobr_retro(nr_sequencia, nr_seq_reajuste, cd_estabelecimento,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec, dt_referencia_mensalidade)
	values (nextval('pls_reajuste_cobr_retro_seq'), nr_seq_reajuste_p, cd_estabelecimento_p,
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		nm_usuario_p, dt_referencia_w);

	dt_referencia_w	:= add_months(dt_referencia_w,1);
	end;
end loop;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
