CREATE OR REPLACE PROCEDURE pls_gerar_reajuste_coletivo ( nr_seq_reajuste_p pls_reajuste.nr_sequencia%type, ie_opcao_p text, ie_excluir_lote_reajuste_p text, nr_seq_motivo_def_p bigint, tx_deflator_p bigint, tx_correto_p bigint, tx_deflator_copartic_p bigint, tx_correto_copartic_p bigint, tx_deflator_copartic_max_p bigint, tx_correto_copartic_max_p bigint, tx_deflator_inscricao_p bigint, tx_correto_inscricao_p bigint, tx_deflator_via_cart_p bigint, tx_correto_via_cart_p bigint, ds_observacao_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_contrato_p pls_contrato.nr_sequencia%type, dt_aplicacao_reajuste_p pls_reajuste.dt_aplicacao_reajuste%type default null) AS $body$
DECLARE


/*
ie_opcao_p
GL	Gerar Lote
AD	Aplicar Deflator
DF	Desfazer Lote
LT	Liberar lote
*/
BEGIN

if (ie_opcao_p = 'GL') then
	pls_reajuste_coletivo_pck.gerar_reajuste(	nr_seq_reajuste_p, null, nm_usuario_p,
							cd_estabelecimento_p);
