	pls_reajuste_coletivo_pck.aplicar_deflator(	nr_seq_reajuste_p, null, nr_seq_motivo_def_p,
							tx_deflator_p, tx_correto_p, tx_deflator_copartic_p,
							tx_correto_copartic_p, tx_deflator_copartic_max_p, tx_correto_copartic_max_p,
