							cd_estabelecimento_p, nr_seq_contrato_p, dt_aplicacao_reajuste_p);
elsif (ie_opcao_p = 'DF') then
	CALL pls_reajuste_coletivo_pck.desfazer_reajuste(	nr_seq_reajuste_p, null, nm_usuario_p,
							cd_estabelecimento_p);
elsif (ie_opcao_p = 'LT') then
	CALL pls_reajuste_coletivo_pck.liberar_reajuste(	nr_seq_reajuste_p, nm_usuario_p, cd_estabelecimento_p);
end if;

commit;

end;
