SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_reenvio_sib ( nr_seq_lote_sib_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_seq_retorno_sib_w		bigint;
ie_tipo_reg_reenvio_w		integer;
nr_seq_motivo_reenvio_w		bigint;
ie_opcao_w			smallint;
ie_carteira_ant_w		varchar(2);
nr_seq_segurado_sib_w		bigint;
ie_registro_w			bigint;
dt_mes_referencia_w		timestamp;
ie_flexibilizado_w		pls_lote_sib.ie_flexibilizado%type;
ie_tipo_flex_w			pls_retorno_sib.ie_tipo_flex%type;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		ie_tipo_reg_reenvio, 
		nr_seq_motivo_reenvio, 
		ie_opcao, 
		ie_carteira_ant, 
		null, 
		coalesce(ie_tipo_flex,'0'), 
		null ie_registro 
	from	pls_retorno_sib 
	where	coalesce(nr_seq_lote_envio::text, '') = '' 
	and	coalesce(ie_enviar_sib,'N')	<> 'N' 
	
union all
 
	SELECT	null, 
		ie_tipo_reg_reenvio, 
		nr_seq_motivo_reenvio, 
		ie_opcao_sib, 
		ie_carteira_ant_sib, 
		nr_sequencia, 
		coalesce(ie_tipo_flex,'0'), 
		null ie_registro 
	from	pls_segurado 
	where	coalesce(ie_reenviar_sib,'N')	<> 'N' 
	and	ie_tipo_segurado in ('B','R') 
	and	cd_estabelecimento = cd_estabelecimento_p 
	
union all
 
	select	a.nr_sequencia, 
		null, 
		null, 
		4, 
		null, 
		a.nr_seq_segurado, 
		coalesce(ie_tipo_flex,'0'), 
		a.ie_registro 
	from	sib_devolucao_erro	a, 
		pls_lote_sib		b 
	where	a.nr_seq_lote		= b.nr_sequencia 
	and	trunc(dt_mes_referencia_w,'dd')	= trunc(b.dt_mesano_referencia,'dd') 
	and	coalesce(a.ie_reenvio,'N')	= 'S' 
	and	b.cd_estabelecimento	= cd_estabelecimento_p 
	order by	ie_registro;


BEGIN 
 
select	dt_mesano_referencia, 
	coalesce(ie_flexibilizado,'N') 
into STRICT	dt_mes_referencia_w, 
	ie_flexibilizado_w 
from	pls_lote_sib 
where	nr_sequencia	= nr_seq_lote_sib_p;
 
open C01;
loop 
fetch C01 into 
	nr_seq_retorno_sib_w, 
	ie_tipo_reg_reenvio_w, 
	nr_seq_motivo_reenvio_w, 
	ie_opcao_w, 
	ie_carteira_ant_w, 
	nr_seq_segurado_sib_w, 
	ie_tipo_flex_w, 
	ie_registro_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (ie_flexibilizado_w = 'S') then 
		--Modo autom√°tico 
		if (ie_tipo_flex_w = '0') then 
			CALL pls_reenviar_sib_aut_flex(	nr_seq_lote_sib_p, nr_seq_retorno_sib_w, ie_tipo_reg_reenvio_w, 
							nr_seq_motivo_reenvio_w, ie_opcao_w, nr_seq_segurado_sib_w, 
							nm_usuario_p, cd_estabelecimento_p, ie_carteira_ant_w);
		--Seleciona atributo 
		else 
			CALL pls_reenviar_sib_flex(	nr_seq_lote_sib_p, nr_seq_retorno_sib_w, ie_tipo_reg_reenvio_w, 
						nr_seq_motivo_reenvio_w, ie_opcao_w, nr_seq_segurado_sib_w, 
						nm_usuario_p, cd_estabelecimento_p, ie_carteira_ant_w);
		end if;
	else 
		CALL pls_reenviar_sib(	nr_seq_lote_sib_p, nr_seq_retorno_sib_w, ie_tipo_reg_reenvio_w, 
					nr_seq_motivo_reenvio_w, ie_opcao_w, nr_seq_segurado_sib_w, 
					nm_usuario_p, cd_estabelecimento_p, ie_carteira_ant_w);
	end if;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_reenvio_sib ( nr_seq_lote_sib_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

