CREATE OR REPLACE PROCEDURE pls_gerar_regra_item_cid ( cd_cid_p text, nr_seq_item_sip_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_commit_p text, ds_retorno_p INOUT text ) AS $body$
DECLARE


nr_Seq_regra_item_w	bigint;

/*
ESSA PROCEDURA Ã‰ USADA NO FONTE DA CORPLS_SP, para gerar regra de item assistencial por CID selecionado.

nr_seq_item_sip_p 	- >  sip_item_assitencial

*/
BEGIN

ds_retorno_p	:= '';

if ( coalesce(nr_seq_item_sip_p,0) > 0) and (cd_cid_p IS NOT NULL AND cd_cid_p::text <> '')  then
	select  nextval('sip_item_assist_regra_seq')
	into STRICT	nr_Seq_regra_item_w
	;

	insert	into sip_item_assist_regra(	nr_Sequencia,ie_situacao, nr_seq_item_assist,
				dt_atualizacao, dt_atualizacao_nrec, cd_doenca_cid,
				nm_usuario, nm_usuario_nrec )
		values (	nr_Seq_regra_item_w,'A',nr_seq_item_sip_p,
				clock_timestamp(), clock_timestamp(), cd_cid_p,
				nm_usuario_p, nm_usuario_p );

	if (ie_commit_p = 'S') then
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
