SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_regra_renov_contr ( nr_seq_contrato_p bigint, ie_commit_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_carteira_w		bigint;
ie_tipo_renovacao_w		varchar(5);
qt_dias_renovacao_w		bigint;
ie_tipo_contrato_w		varchar(2);
ie_tipo_contrato_ww		varchar(2);
qt_registros_w			bigint;
ie_titularidade_w		varchar(10);
ie_limite_dependencia_w		varchar(10);
ie_dependente_venc_titular_w	varchar(10);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_param_regra_carteira
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	((ie_tipo_contrato	= 'O') or (coalesce(ie_tipo_contrato::text, '') = ''))
	order by nr_sequencia;


BEGIN

select	CASE WHEN coalesce(cd_cgc_estipulante::text, '') = '' THEN 'PF'  ELSE 'PJ' END
into STRICT	ie_tipo_contrato_ww
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

open C01;
loop
fetch C01 into
	nr_seq_regra_carteira_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	/*Obter dados da regra da carteira*/

	select	ie_tipo_renovacao,
		coalesce(qt_dias_renovacao,0),
		ie_tipo_contrato,
		coalesce(ie_titularidade,'A'),
		coalesce(ie_limite_dependencia,'N'),
		coalesce(ie_dependente_venc_titular,'N')
	into STRICT	ie_tipo_renovacao_w,
		qt_dias_renovacao_w,
		ie_tipo_contrato_w,
		ie_titularidade_w,
		ie_limite_dependencia_w,
		ie_dependente_venc_titular_w
	from	pls_param_regra_carteira
	where	nr_sequencia	= nr_seq_regra_carteira_w;

	select	count(*)
	into STRICT	qt_registros_w
	from	pls_carteira_renovacao
	where	nr_seq_contrato			= nr_seq_contrato_p
	and	coalesce(ie_tipo_renovacao,'X')	= coalesce(ie_tipo_renovacao_w,'X')
	and	coalesce(ie_titularidade,'A')	 = ie_titularidade_w
	and	qt_meses_renovacao		= qt_dias_renovacao_w;

	if (qt_registros_w	= 0) and
		((ie_tipo_contrato_w	= ie_tipo_contrato_ww) or (ie_tipo_contrato_w	= 'A')) then
		insert into pls_carteira_renovacao(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				cd_estabelecimento,nr_seq_contrato,qt_meses_renovacao,ie_tipo_renovacao,ie_sem_vencimento,
				ie_titularidade,ie_limite_dependencia,ie_dependente_venc_titular)
		values (	nextval('pls_carteira_renovacao_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				cd_estabelecimento_p,nr_seq_contrato_p,qt_dias_renovacao_w,ie_tipo_renovacao_w,CASE WHEN qt_dias_renovacao_w=0 THEN 'S'  ELSE 'N' END ,
				ie_titularidade_w,ie_limite_dependencia_w,ie_dependente_venc_titular_w);
	end if;
	end;
end loop;
close C01;

if (coalesce(ie_commit_p,'S')	= 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
