SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_regra_tab_preco_a800 ( nr_seq_benef_fatura_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_fatura_w			bigint;
nr_seq_segurado_w		bigint;
cd_cooperativa_w		varchar(10);
ie_grau_parentesco_w		varchar(10);
ie_tipo_contratacao_w		varchar(10);
ie_regulamentacao_w		varchar(10);
dt_contratacao_w		timestamp;
ie_acomodacao_w			varchar(10);
nr_seq_contrato_w		bigint;
nr_seq_cooperativa_w		bigint;
dt_nascimento_w			timestamp;
qt_idade_w			bigint;
nr_seq_tabela_w			bigint;
nr_seq_preco_fatura_w		bigint;
vl_preco_atual_w		double precision;
nr_seq_congenere_atend_w	bigint;
sg_estado_w			pessoa_juridica.sg_estado%type;
sg_estado_int_w			pessoa_juridica.sg_estado%type;
ie_tipo_atendimento_w		varchar(10);
cd_estabelecimento_w		bigint;
nr_seq_repasse_w		pls_segurado_repasse.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_seq_tabela
	from	ptu_regra_tabela_a800
	where	clock_timestamp()	between coalesce(dt_inicio_vigencia,clock_timestamp()) and fim_dia(dt_fim_vigencia)
	and	((ie_tipo_contratacao	= ie_tipo_contratacao_w) or (coalesce(ie_tipo_contratacao::text, '') = ''))
	and	((ie_regulamentacao	= ie_regulamentacao_w) or (coalesce(ie_regulamentacao::text, '') = ''))
	and	dt_contratacao_w between coalesce(dt_adesao_inicial,dt_contratacao_w) and coalesce(dt_adesao_final,dt_contratacao_w)
	and	((ie_acomodacao	= ie_acomodacao_w) or (ie_acomodacao = 'T') or (ie_acomodacao = 'N'))
	and	((nr_seq_contrato	= nr_seq_contrato_w) or (coalesce(nr_seq_contrato::text, '') = ''))
	and	((nr_seq_cooperativa	= nr_seq_cooperativa_w) or (coalesce(nr_seq_cooperativa::text, '') = ''))
	and	((ie_abrangencia_repasse = ie_tipo_atendimento_w) or (ie_abrangencia_repasse = 'A'))
	order by coalesce(ie_tipo_contratacao,'-1'),
		coalesce(ie_regulamentacao,'-1'),
		ie_acomodacao,
		coalesce(nr_seq_contrato,-1),
		coalesce(nr_seq_cooperativa,1);

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	ptu_tabela_preco_a800
	where	nr_seq_tabela		= nr_seq_tabela_w
	and	qt_idade_w		>= qt_idade_inicial
	and	qt_idade_w		<= qt_idade_final
	and	coalesce(ie_grau_titularidade,ie_grau_parentesco_w)	= ie_grau_parentesco_w
	order	by	coalesce(ie_grau_titularidade,' ');


BEGIN

select	nr_seq_fatura,
	nr_seq_segurado,
	dt_nascimento
into STRICT	nr_seq_fatura_w,
	nr_seq_segurado_w,
	dt_nascimento_w
from	ptu_fatura_pre_cobranca
where	nr_sequencia	= nr_seq_benef_fatura_p;

if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
	select	a.ie_tipo_contratacao,
		a.ie_regulamentacao,
		b.dt_contratacao,
		a.ie_acomodacao,
		b.nr_seq_contrato,
		coalesce(b.cd_estabelecimento,a.cd_estabelecimento)
	into STRICT	ie_tipo_contratacao_w,
		ie_regulamentacao_w,
		dt_contratacao_w,
		ie_acomodacao_w,
		nr_seq_contrato_w,
		cd_estabelecimento_w
	from	pls_segurado		b,
		pls_plano		a
	where	b.nr_seq_plano		= a.nr_sequencia
	and	b.nr_sequencia		= nr_seq_segurado_w;

	if (ie_acomodacao_w = 'I') then
		ie_acomodacao_w	:= 'A';
	elsif (ie_acomodacao_w = 'C') then
		ie_acomodacao_w	:= 'E';
	end if;

	select	cd_unimed_origem
	into STRICT	cd_cooperativa_w
	from	ptu_fatura_pre
	where	nr_sequencia	= nr_seq_fatura_w;

	select	max(nr_sequencia)
	into STRICT	nr_seq_cooperativa_w
	from	pls_congenere
	where	(cd_cooperativa)::numeric 	= (cd_cooperativa_w)::numeric;

	select	max(nr_sequencia)
	into STRICT	nr_seq_repasse_w
	from	pls_segurado_repasse	A
	where	A.nr_seq_segurado		= nr_seq_segurado_w
	and	a.nr_seq_congenere	= nr_seq_cooperativa_w
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	a.ie_tipo_repasse		= 'P'
	and	((a.dt_fim_repasse = (	SELECT	max(dt_fim_repasse)
					from	pls_segurado_repasse x
					where	x.nr_seq_segurado	= A.nr_seq_segurado
					and	x.nr_seq_congenere	= a.nr_seq_congenere)) or (coalesce(dt_fim_repasse::text, '') = ''));

	if (coalesce(nr_seq_repasse_w,0) > 0) then
		select	max(nr_seq_congenere_atend)
		into STRICT	nr_seq_congenere_atend_w
		from	pls_segurado_repasse
		where	nr_sequencia	= nr_seq_repasse_w;
	end if;

	if (coalesce(nr_seq_congenere_atend_w::text, '') = '') then
		nr_seq_congenere_atend_w	:= nr_seq_cooperativa_w;
	end if;

	select	coalesce(max(sg_estado),'X')
	into STRICT	sg_estado_w
	from	pessoa_juridica
	where	cd_cgc	= (	SELECT	max(cd_cgc_outorgante)
				from	pls_outorgante
				where	cd_estabelecimento	= cd_estabelecimento_w);

	select	coalesce(max(a.sg_estado),'X')
	into STRICT	sg_estado_int_w
	from	pessoa_juridica	a,
		pls_congenere	b
	where	a.cd_cgc	= b.cd_cgc
	and	b.nr_sequencia	= nr_seq_congenere_atend_w;

	if (sg_estado_w	= sg_estado_int_w) then
		ie_tipo_atendimento_w	:= 'E';
	else
		ie_tipo_atendimento_w	:= 'N';
	end if;

	open C01;
	loop
	fetch C01 into
		nr_seq_tabela_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

	if (nr_seq_tabela_w IS NOT NULL AND nr_seq_tabela_w::text <> '') then
		ie_grau_parentesco_w	:= coalesce(substr(pls_obter_garu_dependencia_seg(nr_seq_segurado_w,'C'),1,2),'X');

		qt_idade_w	:= trunc(months_between(clock_timestamp(), trunc(dt_nascimento_w,'month')) / 12);

		open C02;
		loop
		fetch C02 into
			nr_seq_preco_fatura_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
		end loop;
		close C02;

		if (nr_seq_preco_fatura_w IS NOT NULL AND nr_seq_preco_fatura_w::text <> '') then
			select	vl_preco_atual
			into STRICT	vl_preco_atual_w
			from	ptu_tabela_preco_a800
			where	nr_sequencia	= nr_seq_preco_fatura_w;

			update	ptu_fatura_pre_cobranca
			set	nr_seq_preco	= nr_seq_preco_fatura_w,
				vl_mens_gerado	= vl_preco_atual_w
			where	nr_sequencia	= nr_seq_benef_fatura_p;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_regra_tab_preco_a800 ( nr_seq_benef_fatura_p bigint, nm_usuario_p text) FROM PUBLIC;

