CREATE OR REPLACE PROCEDURE pls_gerar_req_interc_benef_pre ( nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar requisição de intercâmbio para requisições negadas de beneficiários repassados em pré-pagamento.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_proc_w		pls_requisicao_proc.nr_sequencia%type;
nr_seq_mat_w		pls_requisicao_mat.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_requisicao_proc
	where	nr_seq_requisicao 	= nr_seq_requisicao_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_requisicao_mat
	where	nr_seq_requisicao 	= nr_seq_requisicao_p;


BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then

	-- Alterar o estágio da requisição para "Aguardando Envio Intercâmbio"
	update	pls_requisicao
	set	ie_estagio		= 10,
		ie_tipo_processo	= 'I'
	where	nr_sequencia		= nr_seq_requisicao_p;

	-- Verificar os procedimentos da requisição e alterar o status dos mesmos para "Aguardando Envio Intercâmbio"
	open C01;
	loop
	fetch C01 into
		nr_seq_proc_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	pls_requisicao_proc
		set	ie_status	= 'I'
		where	nr_sequencia	= nr_seq_proc_w;

		end;
	end loop;
	close C01;

	-- Verificar os materiais da requisição e alterar o status dos mesmos para "Aguardando Envio Intercâmbio"
	open C02;
	loop
	fetch C02 into
		nr_seq_mat_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		update	pls_requisicao_mat
		set	ie_status	= 'I'
		where	nr_sequencia	= nr_seq_mat_w;

		end;
	end loop;
	close C02;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
