		obter_valor_dominio(1669,a.ie_preco) ds_preco,
		a.ie_regulamentacao,
		obter_valor_dominio(2157,a.ie_regulamentacao) ds_regulamentacao,
		a.ie_tipo_contratacao,
		obter_valor_dominio(1666,a.ie_tipo_contratacao) ds_tipo_contratacao,
		a.ie_segmentacao,
		obter_valor_dominio(1665,a.ie_segmentacao) ds_segmentacao,
		r.nr_contrato,
		r.nr_sequencia nr_seq_contrato,
		0 vl_pagar,
		coalesce(b.vl_ajuste, 0) vl_evento,
		0 vl_deferido,
		null dt_liquidacao,
		p.dt_processo dt_contabil,
		coalesce(p.dt_recebimento,p.dt_processo) dt_conhecimento,
		null dt_vencimento,
		c.dt_internacao dt_ocorrencia,
		null nr_titulo,
		b.cd_conta_deb_ajuste,
		b.cd_conta_cred_ajuste,
		s.nr_sequencia nr_seq_segurado,
		c.nr_seq_prestador,
		coalesce(s.nr_seq_titular,s.nr_sequencia) nr_seq_titular,
		CASE WHEN a.ie_tipo_operacao='A' THEN  'SCA'  ELSE coalesce(a.nr_protocolo_ans, a.cd_scpa) END  nr_protocolo_ans,
		CASE WHEN s.ie_tipo_segurado='C' THEN ds_observacao_w WHEN s.ie_tipo_segurado='I' THEN ds_observacao_w WHEN s.ie_tipo_segurado='T' THEN ds_observacao_w  ELSE null END  ds_observacao,
		p.cd_abi cd_abi,
		c.nr_aih nr_aih
	FROM pls_processo p, pls_processo_competencia d, pls_processo_contas_comp b, pls_processo_conta c
LEFT OUTER JOIN pls_segurado s ON (c.nr_seq_segurado = s.nr_sequencia)
LEFT OUTER JOIN pls_contrato r ON (s.nr_seq_contrato = r.nr_sequencia)
LEFT OUTER JOIN pls_plano a ON (s.nr_seq_plano = a.nr_sequencia)
WHERE p.nr_sequencia		= c.nr_seq_processo    and c.nr_sequencia		= b.nr_seq_conta and b.nr_seq_competencia	= d.nr_sequencia and d.dt_competencia between dt_inicial_w and dt_final_w and ((coalesce(ie_tipo_segurado_w, 'X')	= coalesce(s.ie_tipo_segurado, 'X'))
	or (coalesce(ie_tipo_segurado_w, 'X')	= 'X')) and coalesce(c.nr_seq_proc_gru::text, '') = '' and b.ie_tipo_movimentacao = 'P' and coalesce(b.vl_ajuste, 0) <> 0 order by nr_documento;

vet_documento	c_documento%rowtype;

BEGIN



CALL wheb_usuario_pck.set_ie_executar_trigger('N');

select	trunc(max(a.dt_inicial),'dd'),
	fim_dia(max(a.dt_final)),
	max(a.dt_liberacao),
	max(ie_tipo_segurado)
into STRICT	dt_inicial_w,
	dt_final_w,
	dt_liberacao_w,
	ie_tipo_segurado_w
from	ctb_livro_auxiliar	a
where	a.nr_sequencia	= nr_seq_reg_auxiliar_p;

select	substr(obter_desc_expressao(922923),1,255)
into STRICT	ds_observacao_w
;

if (ie_origem_p = 'H' or ie_gerar_arquivo_p = 'N') then
	open c_documento;
	loop
	fetch c_documento into
		vet_documento;
	EXIT WHEN NOT FOUND; /* apply on c_documento */
		begin
		nr_contador_w		:= nr_contador_w + 1;

		if (ie_origem_p = 'H') then
			insert into pls_aux_ress_sus(
				nr_sequencia,
				dt_atualizacao,      
				nm_usuario,             
				dt_atualizacao_nrec,    
				nm_usuario_nrec,        
				nr_seq_reg_auxiliar,    
				nr_documento,           
				ie_tipo_documento,      
				nr_seq_segurado,        
				nr_seq_pagador,         
				nr_seq_prestador,       
				ie_preco,               
				ie_regulamentacao,      
				ie_tipo_contratacao,    
				ie_segmentacao,         
				nr_seq_contrato,        
				vl_pagar,               
				vl_evento,              
				vl_deferido,            
				dt_pagamento,           
				dt_contabil,            
				dt_conhecimento,        
				dt_vencimento, 
				dt_ocorrencia,        
				nr_titulo,              
				cd_conta_debito,        
				cd_conta_credito,       
				nr_contrato,
				nr_registro_produto,
				ds_observacao,
				cd_abi,
				nr_aih,
				ie_tipo_segurado)
			values (
				nextval('pls_aux_ress_sus_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_reg_auxiliar_p,
				vet_documento.nr_documento,
				vet_documento.ie_tipo_documento,
				vet_documento.nr_seq_segurado,
				vet_documento.nr_seq_titular,
				vet_documento.nr_seq_prestador, 
				vet_documento.ie_preco,
				vet_documento.ie_regulamentacao,
				vet_documento.ie_tipo_contratacao,
				vet_documento.ie_segmentacao,
				vet_documento.nr_seq_contrato,
				vet_documento.vl_pagar,
				vet_documento.vl_evento,
				vet_documento.vl_deferido,
				vet_documento.dt_liquidacao,
				vet_documento.dt_contabil,
				vet_documento.dt_conhecimento,
				vet_documento.dt_vencimento,
				vet_documento.dt_ocorrencia,
				vet_documento.nr_titulo,
				vet_documento.cd_conta_deb,
				vet_documento.cd_conta_cred,
				vet_documento.nr_contrato,
				vet_documento.nr_protocolo_ans,
				vet_documento.ds_observacao,
				vet_documento.cd_abi,
				vet_documento.nr_aih,
				vet_documento.ie_tipo_segurado);
		end if;

		if (ie_origem_p = 'D') then	
			insert into w_ctb_livro_aux_event_liq(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_documento,
				cd_beneficiario,
				nm_usuario_evento,
				nr_cpf_cnpj_adic,
				nm_usuario_princ,
				ie_tipo_segurado,
				ds_tipo_segurado,
				nm_prestador,
				ie_tipo_documento,
				ds_tipo_documento,
				ie_preco,
				ds_modalidade_contrat,
				ie_regulamentacao,
				ds_tipo_regulamentacao,
				ie_tipo_contratacao,
				ds_tipo_contratacao,
				ie_segmentacao,
				ds_segmentacao,
				nr_contrato,
				vl_pagar,
				vl_evento,
				vl_glosa,
				dt_pagamento,
				dt_contabilizacao,
				dt_conhecimento,
				dt_vencimento,
				dt_ocorrencia,
				nr_titulo,
				cd_conta_contrapartida,
				cd_conta_contabil,
				ie_tipo_livro,
				nr_linha,
				nr_seq_reg_auxiliar,
				nr_seq_segurado,
				nr_seq_prestador,
				dt_referencia,
				nr_seq_pagador,
				nr_protocolo_ans,
				ds_observacao,
				cd_material_ops,
				ds_operacao)
			values (	
				nextval('w_ctb_livro_aux_event_liq_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				vet_documento.nr_documento,
				vet_documento.cd_beneficiario,
				vet_documento.nm_usuario_evento,
				vet_documento.nr_cpf_cnpj_adic,
				vet_documento.nm_pagador,
				vet_documento.ie_tipo_segurado,
				vet_documento.ds_tipo_segurado,
				vet_documento.nm_prestador,
				vet_documento.ie_tipo_documento,
				vet_documento.ds_tipo_documento,
				vet_documento.ie_preco,
				vet_documento.ds_preco,
				vet_documento.ie_regulamentacao,
				vet_documento.ds_regulamentacao,
				vet_documento.ie_tipo_contratacao,
				vet_documento.ds_tipo_contratacao,
				vet_documento.ie_segmentacao,
				vet_documento.ds_segmentacao,
				vet_documento.nr_contrato,
				vet_documento.vl_pagar,
				vet_documento.vl_evento,
				vet_documento.vl_deferido,
				vet_documento.dt_liquidacao,
				vet_documento.dt_contabil,
				vet_documento.dt_conhecimento,
				vet_documento.dt_vencimento,
				vet_documento.dt_ocorrencia,
				vet_documento.nr_titulo,
				vet_documento.cd_conta_deb,
				vet_documento.cd_conta_cred,
				8,
				nr_contador_w,
				nr_seq_reg_auxiliar_p,
				vet_documento.nr_seq_segurado,
				vet_documento.nr_seq_prestador,
				dt_final_w,
				vet_documento.nr_seq_titular,
				vet_documento.nr_protocolo_ans,
				vet_documento.ds_observacao,
				vet_documento.cd_abi,
				vet_documento.nr_aih);
		end if;

		end;
	end loop;
	close c_documento;
	
	commit;

elsif (ie_origem_p = 'D' and ie_gerar_arquivo_p = 'S') then
	
	nm_arquivo_w	:= 'RegAuxEventosRessarcSUS' || to_char(clock_timestamp(),'ddmmyyyyhh24miss') || nm_usuario_p || '.csv';
	ds_nome_livro_w	:= obter_desc_expressao(785017);
	ds_periodo_w 	:= wheb_mensagem_pck.get_texto(1098702, 'DT_INICIAL=' || dt_inicial_w || ';DT_FINAL=' || dt_final_w || ';DT_LIBERACAO=' || dt_liberacao_w || ';');
	ds_rotulo_w	:= obter_desc_expressao(1075971);

	SELECT * FROM obter_evento_utl_file(1, null, ds_local_w, ds_erro_w) INTO STRICT ds_local_w, ds_erro_w;

	arq_texto_w := utl_file.fopen(ds_local_w,nm_arquivo_w,'W', 32000);

	utl_file.put_line(arq_texto_w, ds_nome_livro_w);
	utl_file.put_line(arq_texto_w, ds_periodo_w);
	utl_file.put_line(arq_texto_w, ds_rotulo_w);
	utl_file.fflush(arq_texto_w);
	for vetl in c_registros_delphi loop
		begin
		utl_file.put_line(arq_texto_w,vetl.ds_linha);
		utl_file.fflush(arq_texto_w);
		end;
	end loop;

end if;

commit;

CALL wheb_usuario_pck.set_ie_executar_trigger('S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
