SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_resumos_lote ( dt_mes_competencia_p timestamp, nr_seq_prestador_p pls_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


dt_mes_competencia_w		timestamp;
ie_status_w			pls_protocolo_conta.ie_status%type;
nr_seq_resumo_w			pls_prot_conta_titulo.nr_sequencia%type;
nr_seq_prestador_w		pls_prestador.nr_sequencia%type;
nr_seq_lote_w			pls_lote_protocolo.nr_sequencia%type;
nr_seq_lote_ww			pls_lote_protocolo.nr_sequencia%type;
ie_status_lote_w		pls_lote_protocolo.ie_status%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
nr_seq_protocolo_w		pls_protocolo_conta.nr_sequencia%type;
qt_lote_pagto_prot_w		integer;

-- na primeira parte do union all retorna apenas se o parâmetro for nulo, na segunda retorna quando o parâmetro passar um prestador
c01 CURSOR(	nr_seq_prestador_pc	pls_prestador.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia,
		b.nr_seq_prestador,
		b.nr_seq_lote,
		a.nr_sequencia
	from	pls_prot_conta_titulo b,
		pls_protocolo_conta a
	where	nr_seq_prestador_pc = 0 -- no delphi é enviado zero quando não tem o prestador
	and	a.nr_sequencia = b.nr_seq_protocolo
	and	a.ie_status = '3'
	and	trunc(dt_mes_competencia,'month') = trunc(dt_mes_competencia_p,'month')
	and	coalesce(a.nr_seq_lote,0) = 0
	and	coalesce(b.nr_seq_lote,0) = 0
	
union all

	SELECT	b.nr_sequencia,
		b.nr_seq_prestador,
		b.nr_seq_lote,
		a.nr_sequencia
	from	pls_prot_conta_titulo b,
		pls_protocolo_conta a
	where	b.nr_seq_prestador = nr_seq_prestador_pc
	and	a.nr_sequencia = b.nr_seq_protocolo
	and	trunc(dt_mes_competencia,'month') = trunc(dt_mes_competencia_p,'month')
	and	a.ie_status = '3'
	and	coalesce(a.nr_seq_lote,0) = 0
	and	coalesce(b.nr_seq_lote,0) = 0;


BEGIN

open C01(coalesce(nr_seq_prestador_p, 0));  --utililizado o vnl 0 pois no delphi é enviado zero quando não tem o prestador
loop
fetch C01 into
	nr_seq_resumo_w,
	nr_seq_prestador_w,
	nr_seq_lote_w,
	nr_seq_protocolo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	begin
		select 	dt_mes_competencia,
			ie_status,
			cd_estabelecimento
		into STRICT	dt_mes_competencia_w,
			ie_status_w,
			cd_estabelecimento_w
		from	pls_protocolo_conta
		where	nr_sequencia = nr_seq_protocolo_w;
	exception
		when others then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(213544,'NR_SEQ_PROTOCOLO_W=' || nr_seq_protocolo_w);
	end;

	select	coalesce(max(ie_status),'P')
	into STRICT	ie_status_lote_w
	from	pls_lote_protocolo
	where	nr_sequencia = nr_seq_lote_w;

	if (ie_status_w = '3') and (ie_status_lote_w = 'P') then

		select 	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_lote_ww
		from	pls_lote_protocolo
		where	nr_seq_prestador = nr_seq_prestador_w
		and	trunc(dt_mes_competencia,'month') = trunc(dt_mes_competencia_w,'month')
		and	ie_status = 'P';

		select	count(1)
		into STRICT	qt_lote_pagto_prot_w
		from	pls_protocolo_conta a
		where	exists (	SELECT	1
				from	pls_conta x,
					pls_conta_medica_resumo y,
					pls_lote_pagamento w
				where	x.nr_seq_protocolo = a.nr_sequencia
				and	y.nr_seq_conta = x.nr_sequencia
				and	y.ie_situacao = 'A'
				and	w.nr_sequencia = y.nr_seq_lote_pgto
				
union all

				SELECT	1
				from	pls_conta x,
					pls_conta_medica_resumo y,
					pls_pp_lote w
				where	x.nr_seq_protocolo = a.nr_sequencia
				and	y.nr_seq_conta = x.nr_sequencia
				and	y.ie_situacao = 'A'
				and	w.nr_sequencia = y.nr_seq_pp_lote)
		and	a.nr_sequencia = nr_seq_protocolo_w  LIMIT 1;

		if (qt_lote_pagto_prot_w = 0) then

			if (nr_seq_lote_ww	= 0) then
				select	nextval('pls_lote_protocolo_seq')
				into STRICT	nr_seq_lote_ww
				;

				insert into pls_lote_protocolo(
					nr_sequencia, cd_estabelecimento, dt_mes_competencia,
					ie_status, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, nm_lote,
					nr_seq_prestador, nr_nota_fiscal
				) values (
					nr_seq_lote_ww, cd_estabelecimento_w, dt_mes_competencia_w,
					'P', clock_timestamp(), nm_usuario_p,
					clock_timestamp(), nm_usuario_p, 'Lote gerado para o prestador ' || nr_seq_prestador_w,
					nr_seq_prestador_w, null
				);
			end if;

			update	pls_prot_conta_titulo
			set	nr_seq_lote	= nr_seq_lote_ww
			where	nr_sequencia	= nr_seq_resumo_w;

			update	pls_protocolo_conta
			set	nr_seq_lote	= nr_seq_lote_ww
			where	nr_sequencia	= nr_seq_protocolo_w;
		end if;
	end if;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_resumos_lote ( dt_mes_competencia_p timestamp, nr_seq_prestador_p pls_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

