CREATE OR REPLACE PROCEDURE pls_gerar_ret_boleto_plusoft ( nr_seq_solicitacao_p bigint, nr_seq_lote_p bigint, nr_seq_mensalidade_p bigint, nr_titulo_p bigint, nm_arquivo_p text, ds_caminho_p text, ds_mensagem_p text) AS $body$
DECLARE


nr_seq_lote_reg_w 	bigint;



BEGIN

	select 	max(nr_sequencia)
	into STRICT	nr_seq_lote_reg_w
	from	w_pls_lote_ret_mens
	where	nr_seq_solicitacao = nr_seq_solicitacao_p
	and	nr_seq_lote = nr_seq_lote_p;

	if (coalesce(nr_seq_lote_reg_w::text, '') = '') then

		select 	nextval('w_pls_lote_ret_mens_seq')
		into STRICT	nr_seq_lote_reg_w
		;

		insert into	w_pls_lote_ret_mens(nr_sequencia , nr_seq_solicitacao, nr_seq_lote)
		values (nr_seq_lote_reg_w, nr_seq_solicitacao_p , nr_seq_lote_p );
	end if;


	insert into	w_pls_retorno_boleto_mens( nr_sequencia, nr_seq_lote_reg, nr_seq_mensalidade,
			nr_titulo, nm_arquivo, ds_caminho, ds_mensagem )
	values (nextval('w_pls_retorno_boleto_mens_seq'), nr_seq_lote_reg_w, nr_seq_mensalidade_p,
 			nr_titulo_p, nm_arquivo_p, ds_caminho_p, ds_mensagem_p );

commit;

end;
