CREATE OR REPLACE PROCEDURE pls_gerar_rps_mensalidade ( nr_seq_mensalidade_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_rps_atual_w	bigint;
nr_rps_w	bigint;


BEGIN

select	max(nr_rps_atual)
into STRICT	nr_rps_atual_w
from	parametro_tesouraria
where	cd_estabelecimento	= cd_estabelecimento_p;

nr_rps_w	:= coalesce(nr_rps_atual_w,0) + 1;

update	parametro_tesouraria
set	nr_rps_atual		= nr_rps_w
where	cd_estabelecimento	= cd_estabelecimento_p;

update	pls_mensalidade
set	nr_rps		= nr_rps_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_mensalidade_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
