SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_senha_generic_web ( nr_sequencia_p bigint, ds_senha_p text, ds_senha_hash_p text, ie_opcao_p text) AS $body$
DECLARE


ds_salt_w		varchar(15);
ds_senha_sha256_w	varchar(64);


BEGIN
/*
ie_opcao_p
'C' - cadastro web cooperado OPS - Gestão de cooperados
'P' - Acesso web OPS - Proposta de Adesão Eletronica

*/
if (ie_opcao_p = 'C') then
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
		select 	replace(replace(dbms_random.string('P', 15), chr(39), ''), ';', '')
		into STRICT	ds_salt_w
		;

		ds_senha_sha256_w := obter_sha2(upper(ds_senha_p) || ds_salt_w, 256);

		update	pls_cooperado_web
                set   	ds_senha = ds_senha_sha256_w,
			ds_tec = ds_salt_w
		where  	nr_sequencia = nr_sequencia_p;


	end if;
elsif (ie_opcao_p = 'P') then
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
		select 	replace(replace(dbms_random.string('P', 15), chr(39), ''), ';', '')
		into STRICT	ds_salt_w
		;

		ds_senha_sha256_w := obter_sha2(upper(ds_senha_p) || ds_salt_w, 256);

		update	pls_proposta_adesao_web
                set   	ds_senha = ds_senha_sha256_w,
			ds_tec = ds_salt_w
		where  	nr_sequencia = nr_sequencia_p;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
