CREATE OR REPLACE PROCEDURE pls_gerar_senha_pf ( cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


ds_senha_w		varchar(20);
ds_senha_atual_w	varchar(20);
dt_nasc_w		timestamp;


BEGIN

select	max(ds_senha)
into STRICT	ds_senha_atual_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (coalesce(ds_senha_atual_w,'0') = '0') then
	
	select max(dt_nascimento)
	into STRICT dt_nasc_w
	from pessoa_fisica
	where cd_pessoa_fisica = cd_pessoa_fisica_p;

	select	obter_senha_pf(dt_nasc_w)
	into STRICT	ds_senha_w
	;
	
	update	pessoa_fisica
	set	ds_senha	= ds_senha_w
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
