SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_seq_congenere ( nr_seq_repasse_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_congenre_w	bigint;
cd_cooperativa_w	varchar(10);


BEGIN

begin
select	max(cd_cooperativa)
into STRICT	cd_cooperativa_w
from	pls_segurado_repasse
where	nr_sequencia	= nr_seq_repasse_p;
exception
when others then
	cd_cooperativa_w	:= null;
end;

if (cd_cooperativa_w IS NOT NULL AND cd_cooperativa_w::text <> '') then
	begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_congenre_w
	from	pls_congenere
	where	cd_cooperativa	= cd_cooperativa_w;
	exception
	when others then
		nr_seq_congenre_w	:= null;
	end;

	if (nr_seq_congenre_w IS NOT NULL AND nr_seq_congenre_w::text <> '') then
		update	pls_segurado_repasse
		set	nr_seq_congenere	= nr_seq_congenre_w
		where	nr_sequencia		= nr_seq_repasse_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
