CREATE OR REPLACE PROCEDURE pls_gerar_seq_segurado_empresa ( nr_seq_segurado_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_incremento_w		bigint;
cd_operadora_empresa_w	bigint;
nr_seq_segurado_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado
	where	cd_operadora_empresa = cd_operadora_empresa_w
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	(nr_sequencial_empresa IS NOT NULL AND nr_sequencial_empresa::text <> '')
	and	nr_sequencia	<> nr_seq_segurado_p
	and	cd_estabelecimento	= cd_estabelecimento_p;

TYPE 		fetch_array IS TABLE OF c01%ROWTYPE;
s_array 	fetch_array;
i		integer := 1;
type Vetor is table of fetch_array index by integer;
Vetor_c01_w			Vetor;


BEGIN

select	max(cd_operadora_empresa)
into STRICT	cd_operadora_empresa_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

qt_incremento_w	:= 0;

open c01;
loop
FETCH C01 BULK COLLECT INTO s_array LIMIT 1000;
	Vetor_c01_w(i) := s_array;
	i := i + 1;
EXIT WHEN NOT FOUND; /* apply on C01 */
END LOOP;
CLOSE C01;

for i in 1..Vetor_c01_w.COUNT loop
	s_array := Vetor_c01_w(i);
	qt_incremento_w	:= qt_incremento_w + s_array.COUNT;
end loop;

qt_incremento_w	:= qt_incremento_w + 1;

update	pls_segurado
set	nr_sequencial_empresa	= qt_incremento_w
where	nr_sequencia		= nr_seq_segurado_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
