SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_simul_perfil_aut ( nr_seq_simulacao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_parametro_com_w	bigint;
nr_seq_regra_perfil_w	bigint;
nr_seq_perfil_w		bigint;
nr_seq_faixa_etaria_w	bigint;
nr_seq_plano_w		bigint;
nr_seq_tipo_fator_w	bigint;
nr_seq_tabela_w		bigint;
qt_registros_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_simul_regra_perfil
	where	nr_seq_regra	= nr_seq_parametro_com_w;

C02 CURSOR FOR
	SELECT	nr_seq_plano
	from	pls_simul_perfil_plano
	where	nr_seq_regra_perfil	= nr_seq_regra_perfil_w;

C03 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_simul_fator_alt_tipo	a
	where	a.cd_estabelecimento	= cd_estabelecimento_p
	and	a.ie_situacao		= 'A'
	and	exists (	SELECT	1
				from	pls_simul_regra_param_item	x,
					pls_simul_regra_perfil		y
				where	x.nr_seq_regra_perfil		= y.nr_sequencia
				and	x.nr_seq_fator_alteracao	= a.nr_sequencia
				and	y.nr_sequencia			= nr_seq_regra_perfil_w);

C04 CURSOR FOR
	SELECT	nr_seq_tabela
	from	pls_regra_simulador_web
	where	nr_seq_plano	= nr_seq_plano_w
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	clock_timestamp() between dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp());


BEGIN

select	max(nr_seq_parametro_com)
into STRICT	nr_seq_parametro_com_w
from	pls_simulacao_preco
where	nr_sequencia	= nr_seq_simulacao_p;

if (nr_seq_parametro_com_w IS NOT NULL AND nr_seq_parametro_com_w::text <> '') then
	select	max(nr_seq_faixa_etaria)
	into STRICT	nr_seq_faixa_etaria_w
	from	pls_simul_regra_param
	where	nr_sequencia	= nr_seq_parametro_com_w;

	open C01;
	loop
	fetch C01 into
		nr_seq_regra_perfil_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	count(1)
		into STRICT	qt_registros_w
		from	pls_simulacao_perfil
		where	nr_seq_simulacao	= nr_seq_simulacao_p
		and	nr_seq_regra_perfil	= nr_seq_regra_perfil_w;

		if (qt_registros_w	= 0) then
			select	nextval('pls_simulacao_perfil_seq')
			into STRICT	nr_seq_perfil_w
			;

			insert into pls_simulacao_perfil(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					nr_seq_simulacao,nr_seq_regra_perfil,nr_seq_faixa_etaria,tx_indice)
			values (	nr_seq_perfil_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					nr_seq_simulacao_p,nr_seq_regra_perfil_w,nr_seq_faixa_etaria_w,1);

			open C02;
			loop
			fetch C02 into
				nr_seq_plano_w;
			EXIT WHEN NOT FOUND; /* apply on C02 */
				begin

				open C04;
				loop
				fetch C04 into
					nr_seq_tabela_w;
				EXIT WHEN NOT FOUND; /* apply on C04 */
					begin
					insert into pls_simul_perfil_tabela(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
							nr_seq_simul_perfil,nr_seq_plano,nr_seq_tabela,ie_tabela_selecionada)
					values (	nextval('pls_simul_perfil_tabela_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
							nr_seq_perfil_w,nr_seq_plano_w,nr_seq_tabela_w,'N');
					CALL pls_gerar_tabela_simulacao(nr_seq_simulacao_p,nr_seq_perfil_w,nr_seq_plano_w,nr_seq_tabela_w,cd_estabelecimento_p,nm_usuario_p);
					end;
				end loop;
				close C04;

				end;
			end loop;
			close C02;

			open C03;
			loop
			fetch C03 into
				nr_seq_tipo_fator_w;
			EXIT WHEN NOT FOUND; /* apply on C03 */
				begin

				insert into pls_simul_param_item(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
						 nr_seq_regra_perfil,nr_seq_tipo_fator,tx_indice)
				values (	nextval('pls_simul_param_item_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
						nr_seq_perfil_w,nr_seq_tipo_fator_w,1);

				end;
			end loop;
			close C03;

			CALL pls_gerar_faixas_etaria_perfil(nr_seq_perfil_w,nr_seq_faixa_etaria_w,nm_usuario_p);
		end if;

		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_simul_perfil_aut ( nr_seq_simulacao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

