SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_solicitacao_lead ( nr_seq_contrato_p bigint, nr_atendimento_p bigint, ie_etapa_solicitacao_p text, ie_chamada_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_segurado_p bigint) AS $body$
DECLARE


/*
	ie_chamada_p - Identificar se esta sendo chamado da rescisao
	R - Rescisao
*/
nr_seq_segurado_w		bigint;
nr_seq_solicitacao_w		bigint;
nm_pessoa_fisica_w		varchar(255);
dt_nascimento_w			timestamp;
nr_telefone_w			varchar(40);
ds_email_w			varchar(255);
nr_cpf_w			varchar(14);
nr_ddd_w			varchar(2);
nr_ddi_w			varchar(2);
sg_uf_w				varchar(2);
cd_municipio_ibge_w		varchar(7);
cd_nacionalidade_w		varchar(8);
nr_reg_geral_estrang_w		varchar(30);
nr_seq_agente_motivador_w	bigint;
nm_contato_w			varchar(50);
nr_telefone_celular_w		varchar(40);
ds_endereco_w			varchar(255);
nr_endereco_w			varchar(10);
ds_complemento_w		varchar(100);
cd_pessoa_fisica_w		varchar(10);
ie_origem_solicitacao_w		varchar(5);
ds_observacao_w			varchar(255);
nr_seq_contrato_w		bigint;
qt_idade_w			bigint;
qt_dias_ativ_canal_com_w	bigint;
nr_seq_motivo_rescisao_w	bigint;
ds_motivo_rescisao_w		varchar(255);
ds_observacao_aux_w		varchar(2000);
ds_bairro_w			varchar(40);

C01 CURSOR FOR
	SELECT	b.cd_pessoa_fisica,
		a.nr_sequencia nr_seq_contrato,
		b.nr_sequencia nr_seq_segurado,
		b.nr_seq_motivo_cancelamento
	from	pls_contrato a,
		pls_segurado b
	where	a.nr_sequencia	= b.nr_seq_contrato
	and	a.nr_sequencia	= nr_seq_contrato_p
	and	coalesce(nr_seq_contrato_p,0)	<> 0
	and	not exists (SELECT 1 from pls_solicitacao_comercial x where x.cd_pf_vinculado = b.cd_pessoa_fisica)
	and	trunc(b.dt_rescisao) = trunc(a.dt_rescisao_contrato) /* Paulo - OS 183475 - 08/12/2009 */
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	
UNION

	select	a.cd_pessoa_fisica,
		null nr_seq_contrato,
		null nr_seq_segurado,
		null
	from	atendimento_paciente a
	where	a.nr_atendimento	= nr_atendimento_p
	and	coalesce(nr_atendimento_p,0)	<> 0
	
union

	select	b.cd_pessoa_fisica,
		a.nr_sequencia nr_seq_contrato,
		b.nr_sequencia nr_seq_segurado,
		b.nr_seq_motivo_cancelamento
	from	pls_contrato a,
		pls_segurado b
	where	a.nr_sequencia	= b.nr_seq_contrato
	and	b.nr_sequencia	= nr_seq_segurado_p
	and	coalesce(nr_seq_segurado_p,0)	<> 0
	and	(b.dt_rescisao IS NOT NULL AND b.dt_rescisao::text <> '')
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');


BEGIN

if (coalesce(nr_seq_contrato_p,0) <> 0) or (coalesce(nr_seq_segurado_p,0) <> 0) then
	ie_origem_solicitacao_w	:= 'R';
	ds_observacao_w	:= 'Gerado a partir do contrato ' || nr_seq_contrato_p || '.';
elsif (coalesce(nr_atendimento_p,0) <> 0) then
	ie_origem_solicitacao_w	:= 'N';
	ds_observacao_w	:= 'Gerado a partir do atendimento ' || nr_atendimento_p || '.';
end if;

select	max(nr_seq_agente_motivador)
into STRICT	nr_seq_agente_motivador_w
from	pls_parametros
where	cd_estabelecimento	= cd_estabelecimento_p;

if (coalesce(nr_seq_agente_motivador_w,0) = 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_agente_motivador_w
	from	pls_agente_motivador
	where	cd_estabelecimento	= cd_estabelecimento_p;
end if;
/* Rescisao */

if (ie_chamada_p = 'R') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_agente_motivador_w
	from	pls_agente_motivador
	where	cd_estabelecimento = cd_estabelecimento_p
	and 	ie_padrao_rescisao = 'S';
end if;

open C01;
loop
fetch C01 into	
	cd_pessoa_fisica_w,
	nr_seq_contrato_w,
	nr_seq_segurado_w,
	nr_seq_motivo_rescisao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('pls_solicitacao_comercial_seq')
	into STRICT	nr_seq_solicitacao_w
	;
	
	select	a.nm_pessoa_fisica,
		a.dt_nascimento,
		substr(obter_compl_pf(a.cd_pessoa_fisica,1,'T'),1,40),
		substr(obter_compl_pf(a.cd_pessoa_fisica,1,'M'),1,255),
		substr(obter_compl_pf(a.cd_pessoa_fisica,1,'C'),1,14),
		coalesce(substr(obter_compl_pf(a.cd_pessoa_fisica,1,'DDT'),1,2),' '),
		coalesce(substr(obter_compl_pf(a.cd_pessoa_fisica,1,'DIT'),1,2),' '),
		substr(obter_compl_pf(a.cd_pessoa_fisica,1,'UF'),1,2),
		substr(obter_compl_pf(a.cd_pessoa_fisica,1,'CDM'),1,7),
		a.cd_nacionalidade,
		a.nr_reg_geral_estrang,
		substr(obter_primeiro_nome(a.nm_pessoa_fisica),1,50),
		a.nr_telefone_celular,
		substr(obter_compl_pf(a.cd_pessoa_fisica,1,'E'),1,40),
		substr(obter_compl_pf(a.cd_pessoa_fisica,1,'NR'),1,10),
		substr(obter_compl_pf(a.cd_pessoa_fisica,1,'CO'),1,100),
		obter_idade(a.dt_nascimento, clock_timestamp(),'A'),
		substr(obter_compl_pf(a.cd_pessoa_fisica,1,'B'),1,40)
	into STRICT	nm_pessoa_fisica_w,
		dt_nascimento_w,
		nr_telefone_w,
		ds_email_w,
		nr_cpf_w,
		nr_ddd_w,
		nr_ddi_w,
		sg_uf_w,
		cd_municipio_ibge_w,
		cd_nacionalidade_w,
		nr_reg_geral_estrang_w,
		nm_contato_w,
		nr_telefone_celular_w,
		ds_endereco_w,
		nr_endereco_w,
		ds_complemento_w,
		qt_idade_w /*aaschlote 30/03/2011 OS - 300982*/
,
		ds_bairro_w
	from	pessoa_fisica a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w;
	
	ds_motivo_rescisao_w	:= '';
	
	if (nr_seq_motivo_rescisao_w IS NOT NULL AND nr_seq_motivo_rescisao_w::text <> '') then
		ds_motivo_rescisao_w	:= obter_desc_expressao(836859) || ': ' || substr(pls_obter_desc_motivo_cancel(nr_seq_motivo_rescisao_w),1,100);
	end if;
	
	ds_observacao_aux_w	:= ds_observacao_w||chr(13)||ds_motivo_rescisao_w;
	
	insert into pls_solicitacao_comercial(	nr_sequencia, cd_estabelecimento, ie_status, nm_pessoa_fisica, dt_nascimento,
						nr_telefone, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
						ds_email, dt_solicitacao, ie_origem_solicitacao, ie_tipo_contratacao, nr_cpf,
						nr_ddi, nr_ddd, sg_uf_municipio, cd_municipio_ibge, cd_nacionalidade,
						nr_reg_geral_estrang, nr_seq_agente_motivador, nm_contato, ie_etapa_solicitacao, nr_celular,
						ds_endereco, nr_endereco, ds_complemento, ds_observacao, nr_seq_segurado,
						nr_seq_contrato, nr_atendimento, qt_idade, ds_bairro, cd_pf_vinculado)
					values (	nr_seq_solicitacao_w, cd_estabelecimento_p, 'PE', nm_pessoa_fisica_w, dt_nascimento_w,
						coalesce(nr_telefone_w,' '), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
						ds_email_w, clock_timestamp(), ie_origem_solicitacao_w, 'I', nr_cpf_w,
						nr_ddi_w, nr_ddd_w, sg_uf_w, cd_municipio_ibge_w, cd_nacionalidade_w,
						nr_reg_geral_estrang_w, nr_seq_agente_motivador_w, nm_contato_w, ie_etapa_solicitacao_p, nr_telefone_celular_w,
						ds_endereco_w, nr_endereco_w, ds_complemento_w, ds_observacao_aux_w, nr_seq_segurado_w,
						nr_seq_contrato_w, nr_atendimento_p, qt_idade_w, ds_bairro_w, cd_pessoa_fisica_w);
	
	CALL pls_atualizar_atividades_canal(nr_seq_solicitacao_w,null,'N',nm_usuario_p);
	/*aaschlote 17/01/2012 OS 403690*/

	CALL pls_inserir_vend_autom_solic(nr_seq_solicitacao_w,'',nm_usuario_p,cd_estabelecimento_p,'N');
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_solicitacao_lead ( nr_seq_contrato_p bigint, nr_atendimento_p bigint, ie_etapa_solicitacao_p text, ie_chamada_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_segurado_p bigint) FROM PUBLIC;

