CREATE OR REPLACE PROCEDURE pls_gerar_solic_lib_mat_med ( nr_seq_usuario_web_p bigint, nr_seq_segurado_p bigint, cd_medico_solicitante_p text, ds_material_p text, ds_marca_p text, ds_fornecedor_p text, nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, vl_unitario_solic_p bigint, nr_ddd_telefone_p text, nr_telefone_p text, ds_unidade_material_p text, ds_email_p text, ds_justificativa_solic_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_item_proc_p bigint, nr_seq_item_mat_p bigint, qt_solicitada_p bigint, cd_brasindice_p text, cd_simpro_p bigint, nr_seq_prestador_p bigint, nr_ramal_p bigint, cd_unidade_medida_p text, ie_tipo_despesa_p text, nr_registro_anvisa_p text, dt_utilizacao_p text, ds_principio_ativo_p text, nm_medico_solic_portal_p text, nm_contato_p text, cd_material_tuss_p bigint, nr_seq_solicitacao_p INOUT bigint) AS $body$
DECLARE


ie_tipo_solic_w		smallint := 1;
nr_seq_solic_w		bigint;


BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	ie_tipo_solic_w	:= 2;
end if;


if (ds_material_p IS NOT NULL AND ds_material_p::text <> '') then

	select	nextval('pls_solic_lib_mat_med_seq')
	into STRICT	nr_seq_solic_w
	;

	insert into pls_solic_lib_mat_med(nr_sequencia, dt_solicitacao,ds_material,
				dt_atualizacao, nm_usuario,dt_atualizacao_nrec,
				nm_usuario_nrec, nr_seq_usuario_web,ds_marca,
				ds_fornecedor, nr_seq_guia,nr_seq_segurado,
				ds_unidade_material, vl_unitario_solic,nr_ddi_telefone,
				nr_ddd_telefone, nr_telefone, ds_email,
				ie_status, cd_medico_solicitante,nr_seq_requisicao,
				ie_tipo_solicitacao, nr_seq_item_proc,nr_seq_item_mat,
				qt_solicitado, cd_brasindice,cd_simpro,
				nr_seq_prestador, nr_ramal,cd_unidade_medida,
				ie_tipo_despesa, nr_registro_anvisa,dt_utilizacao,
				ds_principio_ativo, nm_medico_solic_portal,ie_origem_solicitacao,
				nm_contato, cd_material_tuss)
			values (nr_seq_solic_w, clock_timestamp(),ds_material_p,
				clock_timestamp(), nm_usuario_p,clock_timestamp(),
				nm_usuario_p, nr_seq_usuario_web_p,ds_marca_p,
				ds_fornecedor_p, nr_seq_guia_p,nr_seq_segurado_p,
				ds_unidade_material_p, vl_unitario_solic_p,'55',
				nr_ddd_telefone_p, nr_telefone_p, ds_email_p,
				1, cd_medico_solicitante_p,nr_seq_requisicao_p,
				ie_tipo_solic_w, nr_seq_item_proc_p,nr_seq_item_mat_p,
				qt_solicitada_p, cd_brasindice_p,cd_simpro_p,
				nr_seq_prestador_p, nr_ramal_p,cd_unidade_medida_p,
				ie_tipo_despesa_p, nr_registro_anvisa_p, to_date(dt_utilizacao_p,'dd/mm/yyyy'),
				ds_principio_ativo_p, nm_medico_solic_portal_p,1,
				nm_contato_p, cd_material_tuss_p);

	nr_seq_solicitacao_p := nr_seq_solic_w;

	if (ds_justificativa_solic_p IS NOT NULL AND ds_justificativa_solic_p::text <> '') then
		insert	into pls_solic_lib_historico(nr_sequencia, nr_seq_solic_lib, dt_atualizacao,
			 nm_usuario, ds_observacao, dt_historico,
			 ie_tipo_historico)
		values (nextval('pls_solic_lib_historico_seq'), nr_seq_solicitacao_p, clock_timestamp(),
			 nm_usuario_p, ds_justificativa_solic_p, clock_timestamp(),
			 5);
	end if;

end if;

commit;

end;
$body$
