CREATE OR REPLACE PROCEDURE pls_gerar_solic_portabilidade ( nr_seq_simul_individual_p bigint, nr_seq_comercial_cliente_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_portabilidade_p INOUT bigint) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(100);
vl_preco_origem_w		double precision;
nr_seq_plano_w			bigint;
nr_seq_tabela_w			bigint;
nr_seq_portabilidade_w		bigint;
qt_dias_resposta_w		bigint;
nr_seq_protocolo_atend_w	pls_protocolo_atendimento.nr_sequencia%type;
nr_protocolo_atend_w		pls_protocolo_atendimento.nr_protocolo%type;


BEGIN

if (coalesce(nr_seq_simul_individual_p,0) > 0) then
	qt_dias_resposta_w	:= coalesce(obter_valor_param_usuario(1242, 1, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p),0);
	
	select	cd_pessoa_fisica,
		vl_mensalidade,
		nr_seq_produto,
		nr_seq_tabela
	into STRICT	cd_pessoa_fisica_w,
		vl_preco_origem_w,
		nr_seq_plano_w,
		nr_seq_tabela_w
	from	pls_simulpreco_individual
	where	nr_sequencia = nr_seq_simul_individual_p;
	
	select	nextval('pls_portab_pessoa_seq')
	into STRICT	nr_seq_portabilidade_w
	;
	
	insert into pls_portab_pessoa(	nr_sequencia,
					cd_pessoa_fisica,
					dt_solicitacao,
					dt_resposta,
					vl_preco_origem,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_plano,
					nr_seq_tabela,
					ie_status,
					ie_adimplente,
					cd_estabelecimento,
					nr_seq_comercial_cliente,
					ie_origem_solicitacao)
				values (	nr_seq_portabilidade_w,
					cd_pessoa_fisica_w,
					clock_timestamp(),
					clock_timestamp()+qt_dias_resposta_w,
					vl_preco_origem_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_plano_w,
					nr_seq_tabela_w,
					'U',
					'S',
					cd_estabelecimento_p,
					nr_seq_comercial_cliente_p,
					'T');
	
	SELECT * FROM pls_gravar_protocolo_atend('10', null, null, null, null, null, null, null, nr_seq_portabilidade_w, null, cd_estabelecimento_p, nm_usuario_p, nr_seq_protocolo_atend_w, nr_protocolo_atend_w) INTO STRICT nr_seq_protocolo_atend_w, nr_protocolo_atend_w;
	
	update	pls_simulpreco_individual
	set	nr_seq_portabilidade	= nr_seq_portabilidade_w
	where	nr_sequencia		= nr_seq_simul_individual_p;
	
	nr_seq_portabilidade_p	:= nr_seq_portabilidade_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_solic_portabilidade ( nr_seq_simul_individual_p bigint, nr_seq_comercial_cliente_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_portabilidade_p INOUT bigint) FROM PUBLIC;

