CREATE OR REPLACE PROCEDURE pls_gerar_solic_via_cart_web ( nr_seq_carteira_p bigint, nr_seq_motivo_via_p bigint, cd_funcao_p bigint, ie_tipo_processo_p text, ie_criar_lote_via_adic_p text, nm_usuario_p text) AS $body$
DECLARE

			 
nr_seq_segurado_w	bigint;


BEGIN 
 
if (ie_criar_lote_via_adic_p = '2') then 
	select	max(nr_seq_segurado) 
	into STRICT	nr_seq_segurado_w 
	from	pls_segurado_carteira 
	where	nr_sequencia	= nr_seq_carteira_p;
	 
	if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then 
		CALL pls_incluir_pf_via_adic(nr_seq_segurado_w,'W','P','Lote criado ao solicitar via adicionar do Portal Web', 'N', nm_usuario_p);
		CALL pls_gerar_ci_solic_portal_web(5, null, null, nr_seq_segurado_w, null, null, nm_usuario_p);
	end if;
else 
	CALL pls_gerar_via_carteira(nr_seq_carteira_p,nr_seq_motivo_via_p,nm_usuario_p,cd_funcao_p,ie_tipo_processo_p,'N');
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
