SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_tabela_contrato_sca ( nr_seq_sca_regra_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_contrato_w			timestamp;
dt_inicio_vigencia_sca_w	timestamp;
nr_seq_tabela_w			bigint;
qt_idade_inicial_w		integer;
qt_idade_final_w		integer;
vl_preco_atual_w		double precision;
tx_acrescimo_w			double precision;
qt_registros_w			bigint;
ie_tabela_contrato_w		varchar(1);
nm_estipulante_w		varchar(80);
ie_tipo_contratacao_w		varchar(3);
vl_preco_nao_subsidiado_w	double precision;
ie_preco_w			varchar(2);
nr_seq_contrato_tabela_w	bigint;
nr_contrato_w			bigint;
vl_minimo_w			double precision;
ie_grau_titularidade_w		varchar(2);
qt_vidas_inicial_w		bigint;
qt_vidas_final_w		bigint;
nr_seq_contrato_sca_w		bigint;
nr_seq_plano_sca_w		bigint;
nr_seq_tabela_sca_contrato_w	bigint;

vl_preco_nao_subsid_atual_w	double precision;

c01 CURSOR FOR 
	SELECT	qt_idade_inicial, 
		qt_idade_final, 
		vl_preco_atual, 
		tx_acrescimo, 
		vl_preco_nao_subsidiado, 
		vl_minimo, 
		ie_grau_titularidade, 
		qt_vidas_inicial, 
		qt_vidas_final, 
		vl_preco_nao_subsid_atual 
	from	pls_plano_preco 
	where	nr_seq_tabela	= nr_seq_tabela_sca_contrato_w 
	order by qt_idade_inicial;


BEGIN 
 
select	nr_seq_contrato, 
	nr_seq_plano, 
	nr_seq_tabela, 
	dt_inicio_vigencia 
into STRICT	nr_seq_contrato_sca_w, 
	nr_seq_plano_sca_w, 
	nr_seq_tabela_sca_contrato_w, 
	dt_inicio_vigencia_sca_w 
from	pls_sca_regra_contrato 
where	nr_sequencia	= nr_seq_sca_regra_p;
 
select	nr_contrato, 
	coalesce(dt_contrato,clock_timestamp()), 
	substr(obter_nome_pf_pj(cd_pf_estipulante,cd_cgc_estipulante),1,80) 
into STRICT	nr_contrato_w, 
	dt_contrato_w, 
	nm_estipulante_w 
from	pls_contrato 
where	nr_sequencia	= nr_seq_contrato_sca_w;
 
select	coalesce(max(nr_contrato),0) 
into STRICT	nr_seq_contrato_tabela_w 
from	pls_tabela_preco 
where	nr_sequencia	= nr_seq_tabela_sca_contrato_w;
 
if (nr_seq_plano_sca_w IS NOT NULL AND nr_seq_plano_sca_w::text <> '') and (nr_seq_tabela_sca_contrato_w IS NOT NULL AND nr_seq_tabela_sca_contrato_w::text <> '') and (nr_seq_contrato_tabela_w = 0) then 
	 
	select	nextval('pls_tabela_preco_seq') 
	into STRICT	nr_seq_tabela_w 
	;
	 
	/*aaschlote 26/10/2010 - ao inserir um tabela de preço, não deve ser encerrada a vigencia no dia atual*/
 
	insert into pls_tabela_preco(nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		nm_tabela, 
		dt_inicio_vigencia, 
		nr_seq_plano, 
		dt_liberacao, 
		ie_tabela_base, 
		nr_contrato, 
		nr_seq_tabela_origem, 
		ie_preco_vidas_contrato, 
		ie_calculo_vidas, 
		nr_seq_faixa_etaria) 
	SELECT	nr_seq_tabela_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		'Tabela para o contrato ' || to_char(nr_contrato_w) || ' - ' || nm_estipulante_w, 
		coalesce(dt_inicio_vigencia_sca_w,dt_contrato_w), 
		nr_seq_plano_sca_w, 
		clock_timestamp(), 
		'N', 
		nr_seq_contrato_sca_w, 
		nr_sequencia, 
		ie_preco_vidas_contrato, 
		ie_calculo_vidas, 
		nr_seq_faixa_etaria 
	from	pls_tabela_preco 
	where	nr_sequencia	= nr_seq_tabela_sca_contrato_w;
	 
	open c01;
	loop 
	fetch c01 into 
		qt_idade_inicial_w, 
		qt_idade_final_w, 
		vl_preco_atual_w, 
		tx_acrescimo_w, 
		vl_preco_nao_subsidiado_w, 
		vl_minimo_w, 
		ie_grau_titularidade_w, 
		qt_vidas_inicial_w, 
		qt_vidas_final_w, 
		vl_preco_nao_subsid_atual_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */			
		insert into pls_plano_preco(nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			nr_seq_tabela, 
			qt_idade_inicial, 
			qt_idade_final, 
			vl_preco_inicial, 
			vl_preco_atual, 
			tx_acrescimo, 
			vl_preco_nao_subsidiado, 
			vl_minimo, 
			ie_grau_titularidade, 
			qt_vidas_inicial, 
			qt_vidas_final, 
			vl_preco_nao_subsid_atual) 
		values (nextval('pls_plano_preco_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_tabela_w, 
			qt_idade_inicial_w, 
			qt_idade_final_w, 
			vl_preco_atual_w, 
			vl_preco_atual_w, 
			tx_acrescimo_w, 
			vl_preco_nao_subsidiado_w, 
			vl_minimo_w, 
			ie_grau_titularidade_w, 
			qt_vidas_inicial_w, 
			qt_vidas_final_w, 
			vl_preco_nao_subsid_atual_w);
	end loop;
	close c01;
	 
	update	pls_sca_regra_contrato 
	set	nr_seq_tabela_origem = nr_seq_tabela_sca_contrato_w, 
		nr_seq_tabela		 = nr_seq_tabela_w, 
		nm_usuario		 = nm_usuario_p, 
		dt_atualizacao		 = clock_timestamp() 
	where	nr_sequencia		 = nr_seq_sca_regra_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
