SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_tabela_simulacao ( nr_seq_simulacao_p bigint, nr_seq_perfil_simulacao_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tabela_w			bigint;
qt_idade_inicial_w		integer;
qt_idade_final_w		integer;
vl_preco_atual_w		double precision;
tx_acrescimo_w			double precision;
qt_registros_w			bigint;
ie_tabela_contrato_w		varchar(1);
ie_tipo_contratacao_w		varchar(3);
vl_preco_nao_subsidiado_w	double precision;
ie_preco_w			varchar(2);
vl_minimo_w			double precision;
ie_grau_titularidade_w		varchar(2);
dt_simulacao_w			timestamp;
ie_proposta_adesao_w		varchar(1);

c01 CURSOR FOR
	SELECT	qt_idade_inicial,
		qt_idade_final,
		vl_preco_atual,
		tx_acrescimo,
		vl_preco_nao_subsidiado,
		vl_minimo,
		ie_grau_titularidade
	from	pls_plano_preco
	where	nr_seq_tabela	= nr_seq_tabela_p
	order by qt_idade_inicial;


BEGIN

ie_proposta_adesao_w	:= coalesce(obter_valor_param_usuario(1233, 7, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p),'N');

if (coalesce(nr_seq_tabela_p::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 193332, null );
	/* A tabela não pôde ser gerada pois não foi informada uma tabela base! */

end if;

select	max(ie_preco)
into STRICT	ie_preco_w
from	pls_plano
where	nr_sequencia	= nr_seq_plano_p;

select	max(dt_simulacao)
into STRICT	dt_simulacao_w
from	pls_simulacao_preco
where	nr_sequencia	= nr_seq_simulacao_p;

if (coalesce(ie_preco_w,0) in (1,4)) then
	if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') and (nr_seq_tabela_p IS NOT NULL AND nr_seq_tabela_p::text <> '') then

		select	nextval('pls_tabela_preco_seq')
		into STRICT	nr_seq_tabela_w
		;

		insert into pls_tabela_preco(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nm_tabela,
			dt_inicio_vigencia,
			nr_seq_plano,
			dt_liberacao,
			ie_tabela_base,
			nr_seq_simulacao_preco,
			nr_seq_simul_perfil,
			nr_seq_tabela_simul_orig,
			ie_proposta_adesao,
			nr_seq_faixa_etaria)
		SELECT	nr_seq_tabela_w,
			clock_timestamp(),
			nm_usuario_p,
			'SIMUL - ' || to_char(nr_seq_simulacao_p) || ' - Pfl. - ' || nr_seq_perfil_simulacao_p || ' - '|| nm_tabela,
			dt_simulacao_w,
			nr_seq_plano_p,
			clock_timestamp(),
			'N',
			nr_seq_simulacao_p,
			nr_seq_perfil_simulacao_p,
			nr_sequencia,
			ie_proposta_adesao_w,
			nr_seq_faixa_etaria
		from	pls_tabela_preco
		where	nr_sequencia	= nr_seq_tabela_p;

		open c01;
		loop
		fetch c01 into
			qt_idade_inicial_w,
			qt_idade_final_w,
			vl_preco_atual_w,
			tx_acrescimo_w,
			vl_preco_nao_subsidiado_w,
			vl_minimo_w,
			ie_grau_titularidade_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			insert into pls_plano_preco(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				nr_seq_tabela,
				qt_idade_inicial,
				qt_idade_final,
				vl_preco_inicial,
				vl_preco_atual,
				tx_acrescimo,
				vl_preco_nao_subsidiado,
				vl_minimo,
				ie_grau_titularidade)
			values (nextval('pls_plano_preco_seq'),
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_tabela_w,
				qt_idade_inicial_w,
				qt_idade_final_w,
				vl_preco_atual_w,
				vl_preco_atual_w,
				tx_acrescimo_w,
				vl_preco_nao_subsidiado_w,
				vl_minimo_w,
				ie_grau_titularidade_w);
		end loop;
		close c01;

		update	pls_simul_perfil_tabela
		set	nr_seq_tabela		= nr_seq_tabela_w
		where	nr_seq_simul_perfil	= nr_seq_perfil_simulacao_p
		and	nr_seq_plano		= nr_seq_plano_p
		and	nr_seq_tabela		= nr_seq_tabela_p;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_tabela_simulacao ( nr_seq_simulacao_p bigint, nr_seq_perfil_simulacao_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

