CREATE OR REPLACE PROCEDURE pls_gerar_tab_intercambio_js ( nr_seq_intercambio_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
ie_preco_w		varchar(10);


BEGIN 
 
if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') then 
	select	ie_preco 
	into STRICT	ie_preco_w 
	from	pls_plano 
	where	nr_sequencia	= nr_seq_plano_p;
 
	if (ie_preco_w in ('1','4')) then 
		CALL pls_gerar_tabela_intercambio(nr_seq_intercambio_p,nr_seq_plano_p,nr_seq_tabela_p,cd_estabelecimento_p,'N',nm_usuario_p);
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
