SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_titulos_aberto ( nr_seq_lote_p bigint, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
dt_referencia_w		timestamp;
nr_seq_empresa_w	bigint;
vl_lote_w		double precision;
nr_titulo_w		bigint;
nr_titulo_ww		bigint;
nr_titulo_cobr_w	bigint;
qt_titulo_w		bigint;
qt_titulo_lote_w	bigint;

C01 CURSOR FOR 
	SELECT	a.nr_titulo 
	from	titulo_receber			a, 
		pls_mensalidade			b, 
		pls_contrato_pagador		c, 
		pls_contrato_pagador_fin	d 
	where	a.nr_seq_mensalidade		= b.nr_sequencia 
	and	b.nr_seq_pagador		= c.nr_sequencia 
	and	d.nr_seq_pagador		= c.nr_sequencia 
	and	a.ie_situacao			= '1' 
	and	trunc(b.dt_referencia, 'month')	= dt_referencia_w 
	and	((d.nr_seq_empresa = nr_seq_empresa_w) or (coalesce(nr_seq_empresa_w::text, '') = '')) 
	and	b.dt_referencia	between coalesce(d.dt_inicio_vigencia,b.dt_referencia) and coalesce(d.dt_fim_vigencia,b.dt_referencia);


BEGIN 
 
if (ie_opcao_p = 'G') then 
	select	trunc(dt_referencia, 'month'), 
		nr_seq_empresa 
	into STRICT	dt_referencia_w, 
		nr_seq_empresa_w 
	from	pls_desc_lote_reenvio 
	where	nr_sequencia	= nr_seq_lote_p;
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_titulo_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		select	max(a.nr_titulo), 
			max(a.nr_sequencia) 
		into STRICT	nr_titulo_w, 
			nr_titulo_cobr_w 
		from	titulo_receber_cobr	a, 
			cobranca_escritural	b 
		where	a.nr_seq_cobranca	= b.nr_sequencia 
		and	b.nr_seq_forma_cobranca = 4 
		and	a.nr_titulo		= nr_titulo_ww;
		 
		select	count(*) 
		into STRICT	qt_titulo_w 
		from	pls_desc_reenvio 
		where	nr_titulo	= nr_titulo_w;
		 
		if	((coalesce(nr_titulo_w,0) <> 0) and (coalesce(qt_titulo_w,0) = 0)) then 
			insert	into	pls_desc_reenvio(	nr_sequencia, nr_seq_lote, cd_estabelecimento, 
					nr_titulo, dt_atualizacao, nm_usuario, 
					dt_atualizacao_nrec, nm_usuario_nrec, nr_titulo_cobr) 
				values (	nextval('pls_desc_reenvio_seq'), nr_seq_lote_p, cd_estabelecimento_p, 
					nr_titulo_w, clock_timestamp(), nm_usuario_p, 
					clock_timestamp(), nm_usuario_p, nr_titulo_cobr_w);
		end if;
		end;
	end loop;
	close C01;
	 
	select	sum((obter_dados_titulo_receber(nr_titulo,'V'))::numeric ) 
	into STRICT	vl_lote_w 
	from	pls_desc_reenvio 
	where	nr_seq_lote	= nr_seq_lote_p;
	 
	select	count(*) 
	into STRICT	qt_titulo_lote_w 
	from	pls_desc_reenvio 
	where	nr_seq_lote	= nr_seq_lote_p;
 
	if (coalesce(qt_titulo_lote_w,0) > 0) then 
		update	pls_desc_lote_reenvio 
		set	ie_status	= 'G', 
			vl_lote		= coalesce(vl_lote_w,0) 
		where	nr_sequencia	= nr_seq_lote_p;
	end if;
elsif (ie_opcao_p = 'D') then 
	delete	from	pls_desc_reenvio 
	where	nr_seq_lote	= nr_seq_lote_p;
 
	update	pls_desc_lote_reenvio 
	set	ie_status	= 'P', 
		vl_lote		= 0 
	where	nr_sequencia	= nr_seq_lote_p;
elsif (ie_opcao_p = 'A') then 
	update	pls_desc_lote_reenvio 
	set	ie_status	= 'D' 
	where	nr_sequencia	= nr_seq_lote_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_titulos_aberto ( nr_seq_lote_p bigint, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

