CREATE OR REPLACE PROCEDURE pls_gerar_titulos_mensalidade ( nr_seq_lote_p pls_lote_mensalidade.nr_sequencia%type, dt_emissao_p timestamp, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_log_w	pls_mens_log_geracao.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia nr_seq_mensalidade
	from	pls_mensalidade a
	where	a.nr_seq_lote	= nr_seq_lote_p
	and	coalesce(a.ie_cancelamento::text, '') = ''
	and	not exists (	SELECT	1
				from	titulo_receber x
				where	x.nr_seq_mensalidade = a.nr_sequencia);

BEGIN
CALL pls_verificar_lib_lote_mens(nr_seq_lote_p);

CALL pls_gravar_historico_lote_mens(nr_seq_lote_p, 6, null, nm_usuario_p, 'N');

nr_seq_log_w := pls_mens_gravar_log_geracao(nr_seq_lote_p, '500', 'I', nm_usuario_p, nr_seq_log_w);

for r_c01_w in C01 loop
	begin
	CALL pls_gerar_titulo_receber_mens(r_c01_w.nr_seq_mensalidade, dt_emissao_p, cd_estabelecimento_p, nm_usuario_p);	
	end;
end loop;

update	pls_lote_mensalidade
set	dt_geracao_titulos	= clock_timestamp()
where	nr_sequencia		= nr_seq_lote_p;

nr_seq_log_w := pls_mens_gravar_log_geracao(nr_seq_lote_p, '500', 'F', nm_usuario_p, nr_seq_log_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
