CREATE OR REPLACE PROCEDURE pls_gerar_titulo_intercambio ( nr_seq_lote_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_fatura_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	ptu_fatura 
	where	nr_seq_lote	= nr_seq_lote_p;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_fatura_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL pls_gerar_tit_rec_fatura(nr_seq_fatura_w,nm_usuario_p);
	CALL ptu_a500_gravar_historico(3,'TÃ­tulo gerado pelo usuario: '||nm_usuario_p,nr_seq_lote_p,nr_seq_fatura_w,cd_estabelecimento_p,nm_usuario_p);
	end;
end loop;
close C01;
 
update	ptu_lote_fatura_envio 
set	dt_geracao_titulo	= clock_timestamp(), 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp() 
where	nr_sequencia		= nr_seq_lote_p;
 
commit;
 
end;
