SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_tit_pagar_prov ( nr_seq_provisao_p bigint, dt_vencimento_p timestamp, cd_condicao_pagamento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
dt_base_venc_w			timestamp;
dt_referencia_w			timestamp;
vl_liquido_w			double precision := 0;

ie_forma_pagamento_w		smallint;
ie_acao_venc_nao_util_w		varchar(1) := 'M';
cd_moeda_w			integer;
ie_mes_fechado_w		varchar(1);

ie_origem_tit_reembolso_w	varchar(1);
cd_tipo_taxa_juros_w		bigint;
pr_juros_padrao_w		double precision;
cd_tipo_taxa_multa_w		bigint;
pr_multa_padrao_w		double precision;
nr_seq_trans_fin_baixa_prov_w	bigint;
cd_conta_financ_w		bigint;
cd_cgc_ans_w			varchar(14);

tx_fracao_parcela_w		double precision := 0;
tx_acrescimo_w			double precision := 0;
dt_vencimento_w			timestamp;
vl_titulo_w			double precision := 0;
nr_titulo_w			bigint;
nr_seq_classif_w		bigint;

cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);

C01 CURSOR FOR 
	SELECT	tx_fracao_parcela,	/* A Prazo  */
 
		coalesce(tx_acrescimo,0), 
		Obter_data_vencimento(	dt_base_venc_w, 
					qt_dias_parcela, 
					cd_estabelecimento_p, 
					ie_corrido_util, 
					coalesce(ie_acao_venc_nao_util_w,'M')) 
	from	parcela 
	where	cd_condicao_pagamento 	= cd_condicao_pagamento_p 
	and	coalesce(ie_forma_pagamento_w,3)	<> 1 
	
UNION
 
	SELECT	100, /* A Vista  */
 
		0,	 
		dt_base_venc_w 
	 
	where	coalesce(ie_forma_pagamento_w,3)	= 1;


BEGIN 
 
dt_base_venc_w	:= dt_vencimento_p;
 
select 	dt_competencia, 
	coalesce(vl_peona,0) 
into STRICT	dt_referencia_w, 
	vl_liquido_w 
from 	pls_peona 
where	nr_sequencia = nr_seq_provisao_p;
 
select	pls_obter_se_mes_fechado(dt_referencia_w,'T', cd_estabelecimento_p) 
into STRICT	ie_mes_fechado_w
;
 
if (ie_mes_fechado_w = 'S') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(188988);
end if;
 
select	cd_moeda_padrao 
into STRICT	cd_moeda_w 
from	parametros_contas_pagar 
where	cd_estabelecimento = cd_estabelecimento_p;
 
select	max(ie_forma_pagamento), 
	max(ie_acao_nao_util) 
into STRICT	ie_forma_pagamento_w, 
	ie_acao_venc_nao_util_w 
from	condicao_pagamento 
where	cd_condicao_pagamento = cd_condicao_pagamento_p 
and	ie_situacao      = 'A';
 
select	cd_tipo_taxa_juro, 
	pr_juro_padrao, 
	cd_tipo_taxa_multa, 
	pr_multa_padrao, 
	nr_seq_trans_fin_baixa_prov, 
	cd_conta_financ_prov, 
	cd_cgc_ans 
into STRICT	cd_tipo_taxa_juros_w, 
	pr_juros_padrao_w, 
	cd_tipo_taxa_multa_w, 
	pr_multa_padrao_w, 
	nr_seq_trans_fin_baixa_prov_w, 
	cd_conta_financ_w, 
	cd_cgc_ans_w 
from	pls_parametros 
where	cd_estabelecimento = cd_estabelecimento_p;
 
open c01;
loop 
fetch c01 into 
	tx_fracao_parcela_w, 
	tx_acrescimo_w, 
	dt_vencimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (coalesce(tx_fracao_parcela_w,0) > 0) then 
		vl_titulo_w 	:= ((vl_liquido_w * tx_fracao_parcela_w) / 100);
	end if;
		 
	if (tx_acrescimo_w <> 0) then 
		vl_titulo_w 	:= vl_titulo_w + ((vl_titulo_w * tx_acrescimo_w) / 100);
	end if;
		 
	select	nextval('titulo_pagar_seq') 
	into STRICT	nr_titulo_w 
	;
		 
	insert into titulo_pagar(	nr_titulo, cd_estabelecimento, dt_atualizacao, nm_usuario, 
					dt_emissao, dt_vencimento_original, dt_vencimento_atual, vl_titulo, 
					vl_saldo_titulo, vl_saldo_juros, vl_saldo_multa, cd_moeda, 
					tx_juros, tx_multa, cd_tipo_taxa_juro, cd_tipo_taxa_multa, 
					ie_situacao, ie_origem_titulo, ie_tipo_titulo, cd_cgc, 
					ie_pls, nr_seq_trans_fin_baixa, nr_seq_provisao, ie_status) 
				values (	nr_titulo_w, cd_estabelecimento_p, clock_timestamp(), nm_usuario_p, 
					clock_timestamp(), dt_vencimento_w, dt_vencimento_w, vl_titulo_w, 
					vl_titulo_w, 0, 0, cd_moeda_w, 
					pr_juros_padrao_w, pr_multa_padrao_w, cd_tipo_taxa_juros_w, cd_tipo_taxa_multa_w, 
					'A', '12', '10', cd_cgc_ans_w, 
					'S', nr_seq_trans_fin_baixa_prov_w, nr_seq_provisao_p,'D');
	CALL ATUALIZAR_INCLUSAO_TIT_PAGAR(nr_titulo_w, nm_usuario_p);
	 
	select	coalesce(max(nr_sequencia),0) + 1 
	into STRICT	nr_seq_classif_w 
	from	titulo_pagar_classif 
	where	nr_titulo	= nr_titulo_w;
	 
	insert into titulo_pagar_classif(	dt_atualizacao, nm_usuario, nr_seq_conta_financ, nr_sequencia, 
						nr_titulo, vl_acrescimo, vl_desconto, vl_original, 
						vl_titulo) 
					values (	clock_timestamp(), nm_usuario_p, cd_conta_financ_w, nr_seq_classif_w, 
						nr_titulo_w, 0, 0, vl_titulo_w, 
						vl_titulo_w);
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_tit_pagar_prov ( nr_seq_provisao_p bigint, dt_vencimento_p timestamp, cd_condicao_pagamento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

