CREATE OR REPLACE PROCEDURE pls_gerar_tributos_lote_prot ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
C01 CURSOR(nr_seq_lote_pc	pls_lote_protocolo.nr_sequencia%type) FOR 
	SELECT	nr_sequencia 
	from	pls_lote_protocolo_venc 
	where	nr_seq_lote = nr_seq_lote_pc;
	
BEGIN 
 
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then 
 
	delete 	from pls_lote_prot_venc_trib 
	where	ie_pago_prev = 'V' 
	and	nr_seq_lote_venc in (	SELECT	nr_sequencia 
					from	pls_lote_protocolo_venc 
					where	nr_seq_lote = nr_seq_lote_p);
					 
	for r_C01_w in C01( nr_seq_lote_p ) loop 
		CALL pls_gerar_trib_venc_lote_pag(r_C01_w.nr_sequencia,nm_usuario_p);
	end loop;
end if;
 
/* NÃ£o pode dar commit */
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
