SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_view_regra_mat ( ie_tipo_regra_p text, ie_tipo_tabela_p text) AS $body$
DECLARE


ds_comando_w			varchar(32000);
ds_tipo_tabela_w		varchar(10);
nm_atributo_w			varchar(255);
qt_regra_w			bigint;
ie_ordem_classificacao_w	varchar(1);
ds_compl_order_w		varchar(100);
ie_tipo_atributo_w  tabela_atributo.ie_tipo_atributo%type;
/*	Ie_tipo_tabela tem referencia da tabela se e prestador, co-participacao, intercambio etc
	ie_tipo_regra tem referencia no tipo de item se e material, procedimento ou servico*/
C01 CURSOR FOR
	SELECT	nm_atributo,
		coalesce(ie_ordem_classificacao,'P')
	from	pls_regra_preco_ordem_mat
	where	ie_tipo_preco = ie_tipo_regra_p
	and	ie_tipo_tabela = ie_tipo_tabela_p
	order by nr_seq_apresentacao;
C02 CURSOR FOR
	SELECT  a.nm_atributo,
		a.ie_tipo_atributo
	from  	tabela_atributo  a
	where  	a.nm_tabela 	= 'PLS_REGRA_PRECO_MAT'
	and  	(a.NR_SEQ_APRESENT IS NOT NULL AND a.NR_SEQ_APRESENT::text <> '')
	and  	not exists (  	SELECT  1
				from  	pls_regra_preco_ordem_mat x
				where   x.nm_atributo   	= a.nm_atributo
				and  	x.ie_tipo_preco 	= ie_tipo_regra_p
				and  	x.ie_tipo_tabela  	= ie_tipo_tabela_p)

	and  	a.nr_seq_apresent < (  	select  max(y.nr_seq_apresent)
					from  	tabela_atributo y
					where  	y.nm_tabela 	= 'PLS_REGRA_PRECO_MAT'
					and  	y.nm_atributo   = 'DS_SE_ENTAO')
	and  	upper(a.ie_tipo_atributo) 	not in ('FUNCTION', 'VISUAL')
	and  	a.nm_atributo 		not in ('NR_SEQUENCIA','CD_OPERADORA_EMPRESA')
	and  	ie_tipo_tabela_p 	not in ('I','IP','IC')
	
UNION ALL

	select  a.nm_atributo,
		Substr(Obter_Tipo_Atributo(A.Nr_Sequencia,A.Nm_Atributo),1,15) ie_tipo_atributo
	from  	tabela_visao_atributo 	a,
		tabela_visao 		b
	Where  	A.Nr_Sequencia 		= B.Nr_Sequencia
	and  	b.nm_tabela 		= 'PLS_REGRA_PRECO_MAT'
	and  	(a.NR_SEQ_APRESENT IS NOT NULL AND a.NR_SEQ_APRESENT::text <> '')
	and  	not exists (  	select  1
				from  	pls_regra_preco_ordem_mat  x
				where  	upper(x.nm_atributo)  	= upper(a.nm_atributo)
				and  	x.ie_tipo_preco 	= ie_tipo_regra_p
				and  	x.ie_tipo_tabela  	= ie_tipo_tabela_p)
	and  	a.nr_seq_apresent < (  	select  max(y.nr_seq_apresent)
					from  	tabela_atributo y
					where  	y.nm_tabela 	= 'PLS_REGRA_PRECO_MAT'
					and  	y.nm_atributo   = 'DS_SE_ENTAO')
	and  	a.nm_atributo 		not in ('NR_SEQUENCIA','CD_OPERADORA_EMPRESA')
	and  	ie_tipo_tabela_p 	in ('I','IP','IC')
	and  	a.nr_sequencia 		= 11452
	And  	upper(Substr(Obter_Tipo_Atributo(A.Nr_Sequencia,A.Nm_Atributo),1,15)) Not In ('FUNCTION', 'VISUAL');


BEGIN


ds_tipo_tabela_w	:='prest';
if (ie_tipo_tabela_p 	= 'P')then
	ds_tipo_tabela_w	:='prest';
elsif	((ie_tipo_tabela_p	= 'IC') or (ie_tipo_tabela_p	= 'IP') or (ie_tipo_tabela_p	= 'I')) then
	ds_tipo_tabela_w	:= 'interc';
elsif (ie_tipo_tabela_p	= 'CO') then
	ds_tipo_tabela_w	:= 'copart';
elsif (ie_tipo_tabela_p	= 'R') then
	ds_tipo_tabela_w	:= 'reemb';
elsif (ie_tipo_tabela_p	= 'O') then
	ds_tipo_tabela_w	:= 'oper';
elsif (ie_tipo_tabela_p	= 'CP') then
	ds_tipo_tabela_w	:= 'CP';
end if;

if	((ie_tipo_tabela_p = 'IC') or (ie_tipo_tabela_p = 'IP') or (ie_tipo_tabela_p = 'I')) then
	ds_comando_w	:=	'create or replace view pls_regra_preco_mat_'||ds_tipo_tabela_w||'_v as ' || pls_util_pck.enter_w||
			'	select	a.* ' ||pls_util_pck.enter_w||
			'	from	pls_regra_preco_mat a ' ||pls_util_pck.enter_w||
			'	where	a.ie_situacao = ''A'' '||pls_util_pck.enter_w||
			'	and	a.ie_tipo_tabela	 in(''I'',''IC'',''IP'')';


else
	ds_comando_w	:=	'create or replace view pls_regra_preco_mat_'||ds_tipo_tabela_w||'_v as '|| pls_util_pck.enter_w||
			'	select	a.* ' ||pls_util_pck.enter_w||
			'	from	pls_regra_preco_mat a ' ||pls_util_pck.enter_w||
			'	where	a.ie_situacao = ''A'' '||pls_util_pck.enter_w||
			'	and	a.ie_tipo_tabela	 = ' || chr(39) || ie_tipo_tabela_p || chr(39);
end if;


/* Verificar se existe regra para ordenacao do tipo de regra selecionado (ie_tipo_regra_p) */

select	count(1)
into STRICT	qt_regra_w
from	pls_regra_preco_ordem_mat
where	ie_tipo_preco = ie_tipo_regra_p
and	ie_tipo_tabela = ie_tipo_tabela_p;

if (qt_regra_w > 0) then
	ds_comando_w	:= ds_comando_w ||pls_util_pck.enter_w|| ' order by ';

	/* Concatenar os campos para ordenacao dos registros da view */

	open C01;
	loop
	fetch C01 into
		nm_atributo_w,
		ie_ordem_classificacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select  max(ie_tipo_atributo)
		into STRICT    ie_tipo_atributo_w
		from    tabela_atributo
		where   nm_tabela      = 'PLS_REGRA_PRECO_MAT'
		and     nm_atributo    =  nm_atributo_w;
		
		if (ie_ordem_classificacao_w = 'C') then
			ds_compl_order_w	:= ' Asc , ';
		elsif (ie_ordem_classificacao_w = 'D') then
			ds_compl_order_w	:= ' Desc , ';
		else
			ds_compl_order_w	:= ' , ';
		end if;

		if (substr(nm_atributo_w,1,2) = 'DT') then
			ds_comando_w	:= ds_comando_w ||pls_util_pck.enter_w||nm_atributo_w || ds_compl_order_w;
		elsif (nm_atributo_w		= 'NR_SEQUENCIA') then
			ds_comando_w	:= ds_comando_w ||pls_util_pck.enter_w||nm_atributo_w || ds_compl_order_w;
		elsif (ie_tipo_atributo_w       = 'NUMBER') then
			ds_comando_w	:= ds_comando_w||pls_util_pck.enter_w||'decode( '|| nm_atributo_w || ',null,0,1)'  || ds_compl_order_w;
		else
			ds_comando_w	:= ds_comando_w||pls_util_pck.enter_w||'decode( '|| nm_atributo_w || ',null,''A'',''A'',''A'',''S'',''C'',''B'' )' || ds_compl_order_w;
		end if;

		end;
	end loop;
	close C01;

	/*Concatena os atributos que nao estao  na tabela pls_regra_preco_ordem para que a order by fique correto*/

	open C02;
	loop
	fetch C02 into
		nm_atributo_w,
		ie_tipo_atributo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (substr(nm_atributo_w,1,2) = 'DT') then
			ds_comando_w	:= ds_comando_w ||pls_util_pck.enter_w||  nm_atributo_w || ' , ';
		elsif (ie_tipo_atributo_w       = 'NUMBER') then
			ds_comando_w	:= ds_comando_w ||pls_util_pck.enter_w|| 'decode( '|| nm_atributo_w || ',null,0,1) , ';
		else
			ds_comando_w	:= ds_comando_w ||pls_util_pck.enter_w|| 'decode( '|| nm_atributo_w || ',null,''A'',''A'',''A'',''S'',''C'',''B'' ) , ';
		end if;

		end;
	end loop;
	close C02;
	/* Realizado o substr para retirar a virgula do final do comando */

end if;

CALL exec_sql_dinamico('tasy', substr(ds_comando_w,1,length(ds_comando_w)-2));

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_view_regra_mat ( ie_tipo_regra_p text, ie_tipo_tabela_p text) FROM PUBLIC;

