CREATE OR REPLACE PROCEDURE pls_gerar_vl_correc_monetaria ( nr_seq_pagador_p pls_contrato_pagador.nr_sequencia%type, nr_titulo_p titulo_receber.nr_titulo%type, nr_seq_baixa_p titulo_receber_liq.nr_sequencia%type, vl_item_p bigint, dt_recebimento_p timestamp, ie_commit_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


dt_referencia_w		timestamp;
						

BEGIN

select	add_months(coalesce(max(a.dt_referencia), dt_recebimento_p),1)
into STRICT	dt_referencia_w
from	pls_mensalidade	a
where	a.nr_seq_pagador	= nr_seq_pagador_p
and	coalesce(a.ie_cancelamento::text, '') = '';

insert into pls_segurado_mensalidade(nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
	nm_usuario, nm_usuario_nrec, cd_estabelecimento,
	nr_titulo, nr_seq_baixa, nr_seq_pagador,
	vl_item, ie_tipo_item, ie_situacao,
	dt_referencia, tx_desconto, ie_tipo_lanc)
values (nextval('pls_segurado_mensalidade_seq'), clock_timestamp(), clock_timestamp(),
	nm_usuario_p, nm_usuario_p, cd_estabelecimento_p,
	nr_titulo_p, nr_seq_baixa_p, nr_seq_pagador_p,
	vl_item_p, 44, 'A',
	dt_referencia_w, 0, 'P');

if (coalesce(ie_commit_p, 'N') = 'S') then
	commit;
end if;
	
end;
$body$
