-- DATASOURCE: dbi:Oracle:host=srv-dbora-03.whebdc.com.br;service_name=DEV_1815;port=1521

SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_w_analise_glosa_ocor ( nr_seq_analise_p bigint, nr_seq_conta_p bigint, nr_seq_grupo_atual_p bigint, nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, nr_seq_proc_partic_p bigint, ie_glosando_item_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar a tabela temporária para visualização das glosas e ocorrências dos itens da análise Nova.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_observacao_w			varchar(4000);
ds_glosa_ocorrencia_w		varchar(255);
ds_tipo_w			varchar(255);
ds_status_w			varchar(255);
ds_glosa_vinc_w			varchar(255);
ds_pagamento_w			varchar(255);
ds_faturamento_w		varchar(255);
ds_nivel_liberacao_w		varchar(255);
ds_grupos_analise_w		varchar(255);
nm_grupo_w			varchar(255);
ie_anexo_w			varchar(255);
ds_forma_inativacao_w		varchar(80);
cd_ocorrencia_w			varchar(30);
ie_status_w			varchar(10);
ie_status_cursor_w		varchar(10);
ie_auditoria_w			varchar(10);
ie_situacao_w			varchar(5);
ie_fechar_conta_w		varchar(5);
ie_glosar_pagamento_w		varchar(3);
ie_glosar_faturamento_w		varchar(3);
ie_forma_inativacao_w		varchar(2);
ie_permissao_grupo_w		varchar(1);
ie_finalizar_analise_w		varchar(1);
ie_selecionada_w		varchar(1)	:= null;
nr_seq_motivo_glosa_w		bigint;
nr_seq_ocorrencia_w		bigint;
nr_seq_ocor_benef_w		bigint;
nr_seq_conta_glosa_w		bigint;
nr_sequencia_w			bigint	:= 0;
nr_seq_motivo_vinc_w		bigint	:= null;
nr_seq_nivel_lib_w		bigint;
nr_nivel_liberacao_w		bigint;
nr_seq_glosa_w			bigint;
nr_seq_fluxo_grupo_w		bigint;
nr_seq_grupo_cursor_w		bigint;
qt_concluida_w			bigint;
qt_grupos_w			bigint;
qt_fluxo_w			bigint;
nr_seq_glosa_vinc_w		bigint;
nr_seq_oc_cta_comb_w		w_pls_analise_glosa_ocor.nr_seq_oc_cta_comb%type;

C01 CURSOR FOR
	/* Glosas da conta */

	SELECT	a.nr_sequencia,
		a.nr_seq_motivo_glosa,
		b.cd_motivo_tiss,
		b.ds_motivo_tiss,
		a.ie_situacao,
		a.ie_fechar_conta,
		a.ds_observacao,
		a.ie_forma_inativacao
	from	tiss_motivo_glosa	b,
		pls_conta_glosa		a
	where	a.nr_seq_motivo_glosa	= b.nr_sequencia
	and	a.nr_seq_conta		= nr_seq_conta_p
	and	coalesce(a.nr_seq_ocorrencia_benef::text, '') = ''
	and	coalesce(a.nr_seq_conta_proc::text, '') = ''
	and	coalesce(a.nr_seq_conta_mat::text, '') = ''
	and	coalesce(a.nr_seq_proc_partic::text, '') = ''
	and	coalesce(nr_seq_conta_proc_p::text, '') = ''
	and	coalesce(nr_seq_conta_mat_p::text, '') = ''
	and	coalesce(nr_seq_proc_partic_p::text, '') = ''
	and	coalesce(a.nr_seq_conta_pos_estab::text, '') = ''
	and	not exists (	SELECT	1
					from	pls_ocorrencia_benef x
					where	x.nr_seq_glosa = a.nr_sequencia)
	
union all

	/* Glosas do procedimento */

	select	a.nr_sequencia,
		a.nr_seq_motivo_glosa,
		b.cd_motivo_tiss,
		b.ds_motivo_tiss,
		a.ie_situacao,
		a.ie_fechar_conta,
		a.ds_observacao,
		a.ie_forma_inativacao
	from	tiss_motivo_glosa	b,
		pls_conta_glosa		a
	where	a.nr_seq_motivo_glosa	= b.nr_sequencia
	and	a.nr_seq_conta_proc	= nr_seq_conta_proc_p
	and	coalesce(a.nr_seq_proc_partic::text, '') = ''
	and	coalesce(a.nr_seq_ocorrencia_benef::text, '') = ''
	and	coalesce(a.nr_seq_proc_partic::text, '') = ''
	and	coalesce(nr_seq_proc_partic_p::text, '') = ''
	and	coalesce(a.nr_seq_conta_pos_estab::text, '') = ''
	and	not exists (	select	1
					from	pls_ocorrencia_benef x
					where	x.nr_seq_glosa = a.nr_sequencia)
	
union all

	/* Glosas do material */

	select	a.nr_sequencia,
		a.nr_seq_motivo_glosa,
		b.cd_motivo_tiss,
		b.ds_motivo_tiss,
		a.ie_situacao,
		a.ie_fechar_conta,
		a.ds_observacao,
		a.ie_forma_inativacao
	from	tiss_motivo_glosa	b,
		pls_conta_glosa		a
	where	a.nr_seq_motivo_glosa	= b.nr_sequencia
	and	a.nr_seq_conta_mat	= nr_seq_conta_mat_p
	and	coalesce(a.nr_seq_ocorrencia_benef::text, '') = ''
	and	coalesce(a.nr_seq_conta_pos_estab::text, '') = ''
	and	not exists (	select	1
					from	pls_ocorrencia_benef x
					where	x.nr_seq_glosa = a.nr_sequencia)
	
union all

	/* Glosas do participante */

	select	a.nr_sequencia,
		a.nr_seq_motivo_glosa,
		b.cd_motivo_tiss,
		b.ds_motivo_tiss,
		a.ie_situacao,
		a.ie_fechar_conta,
		a.ds_observacao,
		a.ie_forma_inativacao
	from	tiss_motivo_glosa	b,
		pls_conta_glosa		a
	where	a.nr_seq_motivo_glosa	= b.nr_sequencia
	and	a.nr_seq_proc_partic	= nr_seq_proc_partic_p
	and	coalesce(a.nr_seq_ocorrencia_benef::text, '') = ''
	and	coalesce(a.nr_seq_conta_pos_estab::text, '') = ''
	and	not exists (	select	1
					from	pls_ocorrencia_benef x
					where	x.nr_seq_glosa = a.nr_sequencia);

C02 CURSOR FOR
	/* Ocorrências da conta */

	SELECT	a.nr_sequencia,
		a.nr_seq_ocorrencia,
		b.cd_ocorrencia,
		b.ds_ocorrencia,
		a.ie_situacao,
		a.ie_fechar_conta,
		b.ie_glosar_pagamento,
		b.ie_glosar_faturamento,
		b.nr_seq_nivel_lib,
		a.nr_seq_glosa,
		a.ie_finalizar_analise,
		a.ie_auditoria,
		a.ds_observacao,
		a.ie_forma_inativacao,
		a.nr_seq_oc_cta_comb
	from	pls_ocorrencia		b,
		pls_ocorrencia_benef	a
	where	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	a.nr_seq_conta		= nr_seq_conta_p
	and	coalesce(a.nr_seq_conta_proc::text, '') = ''
	and	coalesce(a.nr_seq_proc::text, '') = ''
	and	coalesce(a.nr_seq_conta_mat::text, '') = ''
	and	coalesce(a.nr_seq_mat::text, '') = ''
	and	coalesce(a.nr_seq_proc_partic::text, '') = ''
	and	coalesce(a.nr_seq_conta_pos_estab::text, '') = ''
	and	coalesce(nr_seq_conta_proc_p::text, '') = ''
	and	coalesce(nr_seq_conta_mat_p::text, '') = ''
	and	coalesce(nr_seq_proc_partic_p::text, '') = ''
	and	not	(((ie_glosando_item_p) = 'S') and (exists (SELECT	1
				from	w_pls_analise_glosa_ocor x
				where	x.nr_seq_ocorrencia	= b.nr_sequencia
				and	x.nr_seq_analise	= nr_seq_analise_P
				and	x.ie_inserido_auditor	= 'S')))
	
union all

	/* Ocorrências do procedimento */

	select	a.nr_sequencia,
		a.nr_seq_ocorrencia,
		b.cd_ocorrencia,
		b.ds_ocorrencia,
		a.ie_situacao,
		a.ie_fechar_conta,
		b.ie_glosar_pagamento,
		b.ie_glosar_faturamento,
		b.nr_seq_nivel_lib,
		a.nr_seq_glosa,
		a.ie_finalizar_analise,
		a.ie_auditoria,
		a.ds_observacao,
		a.ie_forma_inativacao,
		a.nr_seq_oc_cta_comb
	from	pls_ocorrencia b,
		pls_ocorrencia_benef a
	where	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	a.nr_seq_conta_proc	= nr_seq_conta_proc_p
	and	coalesce(a.nr_seq_proc_partic::text, '') = ''
	and	coalesce(nr_seq_proc_partic_p::text, '') = ''
	and	not	(((ie_glosando_item_p) = 'S') and (exists (select	1
				from	w_pls_analise_glosa_ocor x
				where	x.nr_seq_ocorrencia	= b.nr_sequencia
				and	x.nr_seq_analise	= nr_seq_analise_P
				and	x.ie_inserido_auditor	= 'S')))
	
union all

	/* Ocorrências do material */

	select	a.nr_sequencia,
		a.nr_seq_ocorrencia,
		b.cd_ocorrencia,
		b.ds_ocorrencia,
		a.ie_situacao,
		a.ie_fechar_conta,
		b.ie_glosar_pagamento,
		b.ie_glosar_faturamento,
		b.nr_seq_nivel_lib,
		a.nr_seq_glosa,
		a.ie_finalizar_analise,
		a.ie_auditoria,
		a.ds_observacao,
		a.ie_forma_inativacao,
		a.nr_seq_oc_cta_comb
	from	pls_ocorrencia		b,
		pls_ocorrencia_benef	a
	where	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	a.nr_seq_conta_mat	= nr_seq_conta_mat_p
	and	not	(((ie_glosando_item_p) = 'S') and (exists (select	1
				from	w_pls_analise_glosa_ocor x
				where	x.nr_seq_ocorrencia	= b.nr_sequencia
				and	x.nr_seq_analise	= nr_seq_analise_P
				and	x.ie_inserido_auditor	= 'S')))
	
union all

	/* Ocorrências do participante */

	select	a.nr_sequencia,
		a.nr_seq_ocorrencia,
		b.cd_ocorrencia,
		b.ds_ocorrencia,
		a.ie_situacao,
		a.ie_fechar_conta,
		b.ie_glosar_pagamento,
		b.ie_glosar_faturamento,
		b.nr_seq_nivel_lib,
		a.nr_seq_glosa,
		a.ie_finalizar_analise,
		a.ie_auditoria,
		a.ds_observacao,
		a.ie_forma_inativacao,
		a.nr_seq_oc_cta_comb
	from	pls_ocorrencia		b,
		pls_ocorrencia_benef	a
	where	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	a.nr_seq_proc_partic	= nr_seq_proc_partic_p
	and	not	(((ie_glosando_item_p) = 'S') and (exists (select	1
				from	w_pls_analise_glosa_ocor x
				where	x.nr_seq_ocorrencia	= b.nr_sequencia
				and	x.nr_seq_analise	= nr_seq_analise_P
				and	x.ie_inserido_auditor	= 'S')));

/* Fluxo de análise */

C03 CURSOR FOR
	SELECT	a.nr_seq_grupo,
		a.ie_status,
		b.nm_grupo_auditor
	from	pls_grupo_auditor b,
		pls_analise_glo_ocor_grupo a
	where	a.nr_seq_grupo		= b.nr_sequencia
	and	a.nr_seq_analise	= nr_seq_analise_p
	and	a.nr_seq_ocor_benef	= nr_seq_ocor_benef_w
	and	((coalesce(a.ie_fluxo_adic::text, '') = '') or (a.ie_fluxo_adic	= 'N'));


BEGIN
if (ie_glosando_item_p = 'S') then
	delete 	from w_pls_analise_glosa_ocor
	where	nr_seq_analise		= nr_seq_analise_p
	and	coalesce(ie_inserido_auditor,'N') 	!= 'S'
	and	ie_situacao <> 'A';
else
	delete from w_pls_analise_glosa_ocor
	where	nr_seq_analise	= nr_seq_analise_p;
end if;

/* Inserção das glosas */

nr_seq_motivo_glosa_w	:= null;
nr_seq_ocorrencia_w	:= null;
ds_glosa_ocorrencia_w	:= null;
nr_seq_ocor_benef_w	:= null;
nr_seq_conta_glosa_w	:= null;
ie_situacao_w		:= null;
ie_status_w		:= null;
cd_ocorrencia_w		:= null;
ds_tipo_w		:= null;
ds_status_w		:= null;
ds_glosa_vinc_w		:= null;
ie_fechar_conta_w	:= null;
ds_pagamento_w		:= null;
ds_faturamento_w	:= null;


open C01;
loop
fetch C01 into
	nr_seq_conta_glosa_w,
	nr_seq_motivo_glosa_w,
	cd_ocorrencia_w,
	ds_glosa_ocorrencia_w,
	ie_situacao_w,
	ie_fechar_conta_w,
	ds_observacao_w,
	ie_forma_inativacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_forma_inativacao_w	:= null;
	nr_sequencia_w		:= nr_sequencia_w + 1;
	ds_tipo_w		:= 'Glosa';
	ds_pagamento_w		:= 'Impede';
	ds_faturamento_w	:= 'Não impede';

	/* Verificação do status */

	ie_status_w	:= 'F';

	select	count(1)
	into STRICT	qt_fluxo_w
	from	pls_analise_glo_ocor_grupo a
	where	a.nr_seq_analise	= nr_seq_analise_p
	and	a.nr_seq_conta_glosa	= nr_seq_conta_glosa_w
	and	((coalesce(a.ie_fluxo_adic::text, '') = '') or (a.ie_fluxo_adic	= 'N'));

	if (qt_fluxo_w > 0) then
		if (ie_situacao_w = 'I') then
			ie_status_w	:= 'I';
		else
			ie_status_w	:= 'C';
		end if;
	end if;

	ds_status_w	:= null;--obter_valor_dominio(5186,ie_status_w);
	if (ie_status_w IS NOT NULL AND ie_status_w::text <> '') then
		select	ds_valor_dominio
		into STRICT	ds_status_w
		from	valor_dominio
		where	cd_dominio	= 5186
		and	vl_dominio	= ie_status_w;
	end if;

	ie_selecionada_w	:= null;

	if (ie_situacao_w = 'A') then
		ie_selecionada_w	:= 'S';
	end if;

	if (ie_forma_inativacao_w = 'U') then
		ds_forma_inativacao_w	:= 'Usuário';
	elsif (ie_forma_inativacao_w = 'S') then
		ds_forma_inativacao_w	:= 'Sistema';
	elsif (ie_forma_inativacao_w = 'US') then
		ds_forma_inativacao_w	:= 'Usuário e Sistema';
	end if;

	insert into w_pls_analise_glosa_ocor(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_analise,
		nr_seq_conta,
		nr_seq_conta_proc,
		nr_seq_conta_mat,
		nr_seq_proc_partic,
		nr_seq_ocorrencia,
		nr_seq_motivo_glosa,
		ds_glosa_ocorrencia,
		nr_seq_ocor_benef,
		nr_seq_conta_glosa,
		ie_situacao,
		ie_status,
		cd_ocorrencia,
		ds_tipo,
		ie_fechar_conta,
		ds_pagamento,
		ds_faturamento,
		ds_status,
		ds_observacao,
		ie_selecionada,
		ie_forma_inativacao,
		ds_forma_inativacao,
		nr_seq_oc_cta_comb)
	values (nr_sequencia_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_analise_p,
		nr_seq_conta_p,
		nr_seq_conta_proc_p,
		nr_seq_conta_mat_p,
		nr_seq_proc_partic_p,
		nr_seq_ocorrencia_w,
		nr_seq_motivo_glosa_w,
		ds_glosa_ocorrencia_w,
		nr_seq_ocor_benef_w,
		nr_seq_conta_glosa_w,
		ie_situacao_w,
		ie_status_w,
		cd_ocorrencia_w,
		ds_tipo_w,
		ie_fechar_conta_w,
		ds_pagamento_w,
		ds_faturamento_w,
		ds_status_w,
		ds_observacao_w,
		ie_selecionada_w,
		ie_forma_inativacao_w,
		ds_forma_inativacao_w,
		nr_seq_oc_cta_comb_w);
	end;
end loop;
close C01;

/* Inserção das ocorrências */

nr_seq_motivo_glosa_w	:= null;
nr_seq_ocorrencia_w	:= null;
ds_glosa_ocorrencia_w	:= null;
nr_seq_ocor_benef_w	:= null;
nr_seq_conta_glosa_w	:= null;
ie_situacao_w		:= null;
ie_status_w		:= null;
cd_ocorrencia_w		:= null;
ds_tipo_w		:= null;
ds_status_w		:= null;
ds_glosa_vinc_w		:= null;
ie_fechar_conta_w	:= null;
ds_pagamento_w		:= null;
ds_faturamento_w	:= null;

open C02;
loop
fetch C02 into
	nr_seq_ocor_benef_w,
	nr_seq_ocorrencia_w,
	cd_ocorrencia_w,
	ds_glosa_ocorrencia_w,
	ie_situacao_w,
	ie_fechar_conta_w,
	ie_glosar_pagamento_w,
	ie_glosar_faturamento_w,
	nr_seq_nivel_lib_w,
	nr_seq_glosa_w,
	ie_finalizar_analise_w,
	ie_auditoria_w,
	ds_observacao_w,
	ie_forma_inativacao_w,
	nr_seq_oc_cta_comb_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	nr_sequencia_w		:= nr_sequencia_w + 1;
	ds_forma_inativacao_w	:= null;
	ds_tipo_w		:= 'Ocorrência';
	ds_glosa_vinc_w		:= null;
	nr_seq_glosa_vinc_w	:= null;

	if (nr_seq_glosa_w IS NOT NULL AND nr_seq_glosa_w::text <> '') then
		nr_seq_glosa_vinc_w	:= nr_seq_glosa_w;
	else
		select	max(a.nr_sequencia)
		into STRICT	nr_seq_glosa_vinc_w
		from	pls_conta_glosa a
		where	a.nr_seq_ocorrencia_benef	= nr_seq_ocor_benef_w;
	end if;

	if (nr_seq_glosa_vinc_w IS NOT NULL AND nr_seq_glosa_vinc_w::text <> '') then
		select	nr_seq_motivo_glosa,
			b.ds_motivo_tiss
		into STRICT	nr_seq_motivo_vinc_w,
			ds_glosa_vinc_w
		from	tiss_motivo_glosa b,
			pls_conta_glosa a
		where	a.nr_seq_motivo_glosa = b.nr_sequencia
		and	a.nr_sequencia	= nr_seq_glosa_vinc_w;
	end if;

	if (ie_glosar_pagamento_w = 'S') then
		ds_pagamento_w	:= 'Impede';
	else
		ds_pagamento_w	:= 'Não impede';
	end if;

	if (ie_glosar_faturamento_w = 'S') then
		ds_faturamento_w	:= 'Impede';
	else
		ds_faturamento_w	:= 'Não impede';
	end if;

	nr_nivel_liberacao_w	:= null;
	ds_nivel_liberacao_w	:= null;

	if (nr_seq_nivel_lib_w IS NOT NULL AND nr_seq_nivel_lib_w::text <> '') then
		select	a.nr_nivel_liberacao,
			a.ds_nivel_liberacao
		into STRICT	nr_nivel_liberacao_w,
			ds_nivel_liberacao_w
		from	pls_nivel_liberacao a
		where	a.nr_sequencia	= nr_seq_nivel_lib_w;
	end if;

	/* Verificação do status */

	ie_status_w		:= 'F'; /* Inicia como Sem Fluxo */
	/* Cursor do fluxo de análise da ocorrência */

	ie_permissao_grupo_w	:= 'N';
	ds_grupos_analise_w	:= null;
	qt_concluida_w		:= 0;
	qt_grupos_w		:= 0;

	open C03;
	loop
	fetch C03 into
		nr_seq_grupo_cursor_w,
		ie_status_cursor_w,
		nm_grupo_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin
		if (nr_seq_grupo_cursor_w = nr_seq_grupo_atual_p) then
			ie_permissao_grupo_w	:= 'S';
		end if;

		qt_grupos_w	:= qt_grupos_w + 1;

		/* Se tem algum grupo ainda pendente, o status fica pendente de análise*/

		if (ie_status_cursor_w = 'P') then
			ie_status_w	:= 'P';
		else
			qt_concluida_w	:= qt_concluida_w + 1;
		end if;

		/* Apenas listagem descritiva dos grupos de análise da ocorrência */

		if (coalesce(ds_grupos_analise_w::text, '') = '') then
			ds_grupos_analise_w	:= nm_grupo_w;
		else
			ds_grupos_analise_w	:= substr(ds_grupos_analise_w || ', ' || nm_grupo_w, 1, 255);
		end if;
		end;
	end loop;
	close C03;

	if (qt_grupos_w > 0) and (qt_concluida_w = qt_grupos_w) then
		if (ie_situacao_w = 'I') then
			if (ie_auditoria_w = 'N') then
				ie_status_w	:= 'N';
			else
				ie_status_w	:= 'I';
			end if;
		else
			if (ie_auditoria_w = 'N') then
				ie_status_w	:= 'N';
			else
				ie_status_w	:= 'C';
			end if;
		end if;
	else
		if (ie_auditoria_w = 'N') then
			ie_status_w	:= 'N';
		end if;
	end if;

	ds_status_w	:= null;--obter_valor_dominio(5186,ie_status_w);
	if (ie_status_w IS NOT NULL AND ie_status_w::text <> '') then
		select	ds_valor_dominio
		into STRICT	ds_status_w
		from	valor_dominio
		where	cd_dominio	= 5186
		and	vl_dominio	= ie_status_w;
	end if;

	ie_selecionada_w	:= null;

	if (ie_situacao_w = 'A') then
		ie_selecionada_w	:= 'S';
	end if;

	nr_sequencia_w	:= nr_sequencia_w + 1;

	if (ie_forma_inativacao_w = 'U') then
		ds_forma_inativacao_w	:= 'Usuário';
	elsif (ie_forma_inativacao_w = 'S') then
		ds_forma_inativacao_w	:= 'Sistema';
	elsif (ie_forma_inativacao_w = 'US') then
		ds_forma_inativacao_w	:= 'Usuário e Sistema';
	end if;

	select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_anexo_w
	from	pls_ocorrencia_anexo	a
	where	a.nr_seq_ocorrencia	= nr_seq_ocorrencia_w  LIMIT 1;

	insert into w_pls_analise_glosa_ocor(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_analise,
		nr_seq_conta,
		nr_seq_conta_proc,
		nr_seq_conta_mat,
		nr_seq_proc_partic,
		nr_seq_ocorrencia,
		nr_seq_motivo_glosa,
		ds_glosa_ocorrencia,
		nr_seq_ocor_benef,
		nr_seq_conta_glosa,
		ie_situacao,
		ie_status,
		cd_ocorrencia,
		ds_tipo,
		ds_glosa_vinc,
		ie_fechar_conta,
		nr_nivel_liberacao,
		ds_status,
		ds_nivel_liberacao,
		ds_pagamento,
		ds_faturamento,
		ie_permissao_grupo,
		ds_grupos_analise,
		ie_finalizar_analise,
		ds_observacao,
		ie_selecionada,
		ie_forma_inativacao,
		ds_forma_inativacao,
		ie_anexo,
		nr_seq_oc_cta_comb)
	values (nr_sequencia_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_analise_p,
		nr_seq_conta_p,
		nr_seq_conta_proc_p,
		nr_seq_conta_mat_p,
		nr_seq_proc_partic_p,
		nr_seq_ocorrencia_w,
		nr_seq_motivo_glosa_w,
		ds_glosa_ocorrencia_w,
		nr_seq_ocor_benef_w,
		nr_seq_conta_glosa_w,
		ie_situacao_w,
		ie_status_w,
		cd_ocorrencia_w,
		ds_tipo_w,
		ds_glosa_vinc_w,
		ie_fechar_conta_w,
		nr_nivel_liberacao_w,
		ds_status_w,
		ds_nivel_liberacao_w,
		ds_pagamento_w,
		ds_faturamento_w,
		ie_permissao_grupo_w,
		ds_grupos_analise_w,
		CASE WHEN ie_finalizar_analise_w='S' THEN null  ELSE ie_finalizar_analise_w END ,
		ds_observacao_w,
		ie_selecionada_w,
		ie_forma_inativacao_w,
		ds_forma_inativacao_w,
		ie_anexo_w,
		nr_seq_oc_cta_comb_w);
	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_w_analise_glosa_ocor ( nr_seq_analise_p bigint, nr_seq_conta_p bigint, nr_seq_grupo_atual_p bigint, nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, nr_seq_proc_partic_p bigint, ie_glosando_item_p text, nm_usuario_p text) FROM PUBLIC;

