CREATE OR REPLACE PROCEDURE pls_gerar_w_atend_ocorr_dia ( dt_inicio_pesquisa_p timestamp, dt_fim_pesquisa_p timestamp, nr_seq_grupo_atend_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_tipo_ocorr_w		bigint;
qt_ocorrencia_w			smallint;
dt_pesquisa_w			timestamp;
ie_gestao_call_center_w		pls_controle_estab.ie_gestao_call_center%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_tipo_ocorrencia
	order by ds_tipo_ocorrencia;


BEGIN

if (trunc(dt_inicio_pesquisa_p, 'month') <> trunc(dt_fim_pesquisa_p, 'month')) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(331491);
end if;

delete from w_call_center_relatorio where nm_usuario = nm_usuario_p;

dt_pesquisa_w	:= dt_inicio_pesquisa_p;

/* Verifica o controle de estabelecimento */

select	coalesce(max(ie_gestao_call_center),'N')
into STRICT	ie_gestao_call_center_w
from	pls_controle_estab;

open C01;
loop
fetch C01 into
	nr_seq_tipo_ocorr_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	while dt_pesquisa_w <= dt_fim_pesquisa_p loop
		begin

		if (ie_gestao_call_center_w = 'S') then
			select	sum(count(b.nr_sequencia))
			into STRICT	qt_ocorrencia_w
			from 	pls_atendimento b,
				pls_atendimento_evento a,
				pls_operador c
			where	a.nr_seq_atendimento 				= b.nr_sequencia
			and	b.nr_seq_operador				= c.nr_sequencia
			and	a.nr_seq_tipo_ocorrencia			= nr_seq_tipo_ocorr_w
			and	trunc(coalesce(b.dt_fim_atendimento, b.dt_inicio))	= dt_pesquisa_w
			and	((c.nr_seq_grupo_atend = nr_seq_grupo_atend_p) or (coalesce(nr_seq_grupo_atend_p::text, '') = ''))
			and	b.cd_estabelecimento = cd_estabelecimento_p
			group by 1;


		else
			select	sum(count(b.nr_sequencia))
			into STRICT	qt_ocorrencia_w
			from 	pls_atendimento b,
				pls_atendimento_evento a,
				pls_operador c
			where	a.nr_seq_atendimento 				= b.nr_sequencia
			and	b.nr_seq_operador				= c.nr_sequencia
			and	a.nr_seq_tipo_ocorrencia			= nr_seq_tipo_ocorr_w
			and	trunc(coalesce(b.dt_fim_atendimento, b.dt_inicio))	= dt_pesquisa_w
			and	((c.nr_seq_grupo_atend = nr_seq_grupo_atend_p) or (coalesce(nr_seq_grupo_atend_p::text, '') = ''))
			group by 1;
		end if;

		insert	into w_call_center_relatorio(nr_sequencia, nm_usuario, nr_seq_tipo_ocorrencia,
			 qt_ocorrencia, dt_ocorrencia, dt_atualizacao)
		values (nextval('w_call_center_relatorio_seq'), nm_usuario_p, nr_seq_tipo_ocorr_w,
			 qt_ocorrencia_w, dt_pesquisa_w, clock_timestamp());

		dt_pesquisa_w	:= dt_pesquisa_w + 1;

		end;
	end loop;

	dt_pesquisa_w	:= dt_inicio_pesquisa_p;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_w_atend_ocorr_dia ( dt_inicio_pesquisa_p timestamp, dt_fim_pesquisa_p timestamp, nr_seq_grupo_atend_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

