CREATE OR REPLACE PROCEDURE pls_gerar_w_fatura_refat (nr_seq_conta_p bigint, ie_acao_p text, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE



/*
ie_acao_p
	'V' - Vincular
	'D' - Desvincular

*/
BEGIN

if (ie_acao_p = 'V') then
	insert  into w_pls_fatura_refat(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_conta)
	values (nextval('w_pls_fatura_refat_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_conta_p);
elsif (ie_acao_p = 'D') then
	delete from w_pls_fatura_refat
	where	nr_seq_conta	= nr_seq_conta_p;
end if;

if (coalesce(ie_commit_p,'S') = 'S') then
	commit;
end if;

end;
$body$
