CREATE OR REPLACE PROCEDURE pls_gerar_w_relat_faixa_etaria ( dt_referencia_p text, nm_usuario_p text) AS $body$
DECLARE




nr_idade_w		integer;
ie_tipo_contratacao_w	varchar(2);
ie_regulamentacao_w	varchar(2);
qt_beneficiarios_w	bigint;
ie_faixa_etaria_w	integer;
ie_acomodacao_w		varchar(2);


C01 CURSOR FOR
	SELECT	pls_obter_idade_segurado(a.nr_sequencia,clock_timestamp(),'A'),
		c.ie_tipo_contratacao,
		c.ie_regulamentacao,
		c.ie_acomodacao
	from	pls_contrato b,
		pls_plano c,
		pls_segurado a
	where	a.nr_seq_contrato	= b.nr_sequencia
	and	a.nr_seq_plano		= c.nr_sequencia
	and	a.dt_rescisao		> dt_referencia_p
	order by pls_obter_idade_segurado(a.nr_sequencia,clock_timestamp(),'A');


BEGIN

delete 	from  w_relat_faixa_etaria
where 	nm_usuario = nm_usuario_p;

open C01;
loop
fetch C01 into
	nr_idade_w,
	ie_tipo_contratacao_w,
	ie_regulamentacao_w,
	ie_acomodacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_idade_w < 10) then
		ie_faixa_etaria_w	:= 1;
	elsif (nr_idade_w < 20) then
		ie_faixa_etaria_w	:= 2;
	elsif (nr_idade_w < 30) then
		ie_faixa_etaria_w	:= 3;
	elsif (nr_idade_w < 40) then
		ie_faixa_etaria_w	:= 4;
	elsif (nr_idade_w < 50) then
		ie_faixa_etaria_w	:= 5;
	elsif (nr_idade_w < 60) then
		ie_faixa_etaria_w	:= 6;
	elsif (nr_idade_w < 70) then
		ie_faixa_etaria_w	:= 7;
	elsif (nr_idade_w < 80) then
		ie_faixa_etaria_w	:= 8;
	elsif (nr_idade_w < 90) then
		ie_faixa_etaria_w	:= 9;
	elsif (nr_idade_w >= 90) then
		ie_faixa_etaria_w	:= 10;
	end if;

	insert into w_relat_faixa_etaria(nr_sequencia,
		ie_tipo_contratacao,
		ie_regulamentacao,
		ie_acomodacao,
		ie_faixa_etaria,
		dt_atualizacao,
		nm_usuario,
		qt_beneficiarios)
	values (nextval('w_relat_faixa_etaria_seq'),
		ie_tipo_contratacao_w,
		ie_regulamentacao_w,
		ie_acomodacao_w,
		ie_faixa_etaria_w,
		clock_timestamp(),
		nm_usuario_p,
		1);
	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
