CREATE OR REPLACE PROCEDURE pls_gera_pos_ressarcimento ( nr_seq_conta_p pls_processo_conta.nr_sequencia%type, nm_usuario_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_pagador_w	pls_segurado.nr_seq_pagador%type;


BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then

	select	max(a.nr_seq_pagador)
	into STRICT	nr_seq_pagador_w
	from	pls_segurado a,
		pls_processo_conta b
	where	a.nr_sequencia = b.nr_seq_segurado
	and	b.nr_sequencia = nr_seq_conta_p;

	insert 	into pls_segurado_mensalidade(	nr_sequencia,			cd_estabelecimento,	dt_atualizacao,
						dt_atualizacao_nrec,		ie_situacao,		nm_usuario,
						nm_usuario_nrec, 		nr_seq_segurado, 	vl_item,
						ie_tipo_item,			dt_referencia, 		nr_seq_pagador,
						ds_observacao,
						nr_seq_processo_conta)

		( 	SELECT	nextval('pls_segurado_mensalidade_seq'),		cd_estabelecimento_p,	clock_timestamp(),
				clock_timestamp(),					'A',			nm_usuario_p,
				nm_usuario_p,					nr_seq_segurado, 	vl_ressarcir,
				39,						add_months(clock_timestamp(), 1),		nr_seq_pagador_w,
				'Item gerado através da função OPS - Controle de processo de ressarcimento ao sus.',
				nr_seq_conta_p
			from	pls_processo_conta
			where	nr_sequencia = nr_seq_conta_p);



end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
