	begin
	update	pls_rpc_lote
	set	nm_arquivo	= nm_arquivo_p
	where	nr_sequencia	= nr_seq_lote_p;
	exception
	when others then
		ds_erro_w	:= '';
	end;
end if;

commit;

end;
