CREATE OR REPLACE PROCEDURE pls_gravar_carga_pagador ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_contrato_w		bigint;
cd_cgc_w			varchar(14);
cd_pessoa_fisica_w		varchar(10);
ie_tipo_pagador_w		varchar(1);
ie_envia_cobranca_w		varchar(1);
nr_seq_forma_cobranca_w		varchar(2);
cd_banco_w			integer;
cd_agencia_bancaria_w		varchar(8);
ie_digito_agencia_w		varchar(1);
cd_conta_w			varchar(20);
ie_digito_conta_w		varchar(2);
dt_dia_vencimento_w		smallint;
ie_endereco_boleto_w		varchar(4);
ie_calc_primeira_mens_w		varchar(1);
cd_condicao_pagamento_w		bigint;
dt_primeira_mensalidade_w	timestamp;
ie_calculo_proporcional_w	varchar(1);
nr_seq_conta_banco_w		bigint;
ie_pessoa_comprovante_w		varchar(2);
dt_rescisao_w			timestamp;
nr_seq_motivo_cancelamento_w	bigint;
ie_notificacao_w		varchar(2);
dt_reativacao_w			timestamp;
nr_seq_pagador_w		bigint;
cd_tipo_portador_w		bigint;
cd_portador_w			bigint;

C01 CURSOR FOR
	SELECT	nr_seq_contrato,
		cd_cgc,
		cd_pessoa_fisica,
		coalesce(ie_tipo_pagador,'P'),
		ie_envia_cobranca,
		coalesce(nr_seq_forma_cobranca,'1'),
		cd_banco,
		cd_agencia_bancaria,
		ie_digito_agencia,
		cd_conta,
		ie_digito_conta,
		coalesce(dt_dia_vencimento,10),
		coalesce(ie_endereco_boleto,'PFR'),
		coalesce(ie_calc_primeira_mens,'I'),
		cd_condicao_pagamento,
		dt_primeira_mensalidade,
		ie_calculo_proporcional,
		nr_seq_conta_banco,
		ie_pessoa_comprovante,
		dt_rescisao,
		nr_seq_motivo_cancelamento,
		ie_notificacao,
		dt_reativacao,
		cd_tipo_portador,
		cd_portador
	from	w_pls_carga_pagador;


BEGIN

open C01;
loop
fetch C01 into	
	nr_seq_contrato_w,
	cd_cgc_w,
	cd_pessoa_fisica_w,
	ie_tipo_pagador_w,
	ie_envia_cobranca_w,
	nr_seq_forma_cobranca_w,
	cd_banco_w,
	cd_agencia_bancaria_w,
	ie_digito_agencia_w,
	cd_conta_w,
	ie_digito_conta_w,
	dt_dia_vencimento_w,
	ie_endereco_boleto_w,
	ie_calc_primeira_mens_w,
	cd_condicao_pagamento_w,
	dt_primeira_mensalidade_w,
	ie_calculo_proporcional_w,
	nr_seq_conta_banco_w,
	ie_pessoa_comprovante_w,
	dt_rescisao_w,
	nr_seq_motivo_cancelamento_w,
	ie_notificacao_w,
	dt_reativacao_w,
	cd_tipo_portador_w,
	cd_portador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	select	nextval('pls_contrato_pagador_seq')
	into STRICT	nr_seq_pagador_w
	;
	
	insert into pls_contrato_pagador(nr_sequencia, nr_seq_contrato, ie_tipo_pagador,
		ie_endereco_boleto, ie_calc_primeira_mens, dt_atualizacao,
		nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		dt_primeira_mensalidade, ie_calculo_proporcional, cd_cgc,
		cd_pessoa_fisica, ie_pessoa_comprovante, ie_envia_cobranca,
		dt_rescisao, nr_seq_motivo_cancelamento, ie_notificacao,
		dt_reativacao, ie_receber_sms)
	values (nr_seq_pagador_w, nr_seq_contrato_w, ie_tipo_pagador_w,
		ie_endereco_boleto_w, ie_calc_primeira_mens_w, clock_timestamp(),
		nm_usuario_p, clock_timestamp(), nm_usuario_p,
		dt_primeira_mensalidade_w, ie_calculo_proporcional_w, cd_cgc_w,
		cd_pessoa_fisica_w, ie_pessoa_comprovante_w, ie_envia_cobranca_w,
		dt_rescisao_w, nr_seq_motivo_cancelamento_w, ie_notificacao_w,
		dt_reativacao_w, 'S');

	insert into pls_contrato_pagador_fin(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
						nm_usuario_nrec, nr_seq_pagador, nr_seq_forma_cobranca, dt_inicio_vigencia,
						cd_banco, nr_seq_conta_banco, cd_agencia_bancaria, ie_digito_agencia,
						cd_conta, ie_digito_conta, dt_dia_vencimento, cd_tipo_portador, cd_portador)
					values (	nextval('pls_contrato_pagador_fin_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(),
						nm_usuario_p, nr_seq_pagador_w, nr_seq_forma_cobranca_w, trunc(clock_timestamp()),
						cd_banco_w, nr_seq_conta_banco_w, cd_agencia_bancaria_w, ie_digito_agencia_w,
						cd_conta_w, ie_digito_conta_w, dt_dia_vencimento_w, cd_tipo_portador_w, cd_portador_w);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
