CREATE OR REPLACE PROCEDURE pls_gravar_carteira_devolucao ( cd_usuario_plano_p text, nr_via_ed_p bigint, dt_validade_cartao_p timestamp, nr_seq_lote_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_carteirinha_w		bigint;
qt_carteira_lote_w		bigint;
dt_devolucao_w			timestamp;
ie_permite_devolucao_sem_via_w	varchar(1);
ie_considerar_validade_cart_w	varchar(1);
dt_validade_cartao_w		timestamp;


BEGIN

ie_permite_devolucao_sem_via_w		:= coalesce(obter_valor_param_usuario(1226, 11, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p),'S');
ie_considerar_validade_cart_w		:= coalesce(obter_valor_param_usuario(1226, 11, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p),'N');
dt_validade_cartao_w			:= dt_validade_cartao_p;

if (ie_considerar_validade_cart_w = 'S') then
	select	max(dt_validade_carteira)
	into STRICT	dt_validade_cartao_w
	from	pls_segurado_carteira
	where	cd_usuario_plano = cd_usuario_plano_p
	and	nr_via_solicitacao = nr_via_ed_p;
	
	if (coalesce(dt_validade_cartao_w::text, '') = '') then
		select	max(dt_validade)
		into STRICT	dt_validade_cartao_w
		from	pls_segurado_cart_ant
		where	cd_usuario_ant = cd_usuario_plano_p
		and	nr_via_anterior = nr_via_ed_p;
	end if;
end if;

if (ie_permite_devolucao_sem_via_w = 'N') and
	((coalesce(nr_via_ed_p,0) = 0) or (coalesce(dt_validade_cartao_w::text, '') = '')) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(362228); --Nao e permitida a devolucao sem informar via e validade do cartao. Parametro [11].
end if;

if (coalesce(cd_usuario_plano_p,'0') = '0') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(214496);
end if;

select	count(1)
into STRICT	qt_carteirinha_w
from	pls_segurado_carteira
where	cd_usuario_plano	= cd_usuario_plano_p;

if (qt_carteirinha_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(214497);
end if;

select	max(dt_devolucao)
into STRICT	dt_devolucao_w
from	pls_cart_lote_devolucao
where	nr_sequencia	= nr_seq_lote_p;

if (dt_devolucao_w IS NOT NULL AND dt_devolucao_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(214498);
end if;

select	count(1)
into STRICT	qt_carteira_lote_w
from	pls_carteira_devolucao
where	nr_seq_lote		= nr_seq_lote_p
and	cd_usuario_plano	= cd_usuario_plano_p;

if (coalesce(qt_carteira_lote_w,0) > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(214499);
end if;

insert	into	pls_carteira_devolucao(	nr_sequencia, nr_seq_lote, cd_estabelecimento,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec, cd_usuario_plano, nr_via, dt_validade_carteira)
values (	nextval('pls_carteira_devolucao_seq'), nr_seq_lote_p, cd_estabelecimento_p,
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		nm_usuario_p, cd_usuario_plano_p, nr_via_ed_p, dt_validade_cartao_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
