CREATE OR REPLACE PROCEDURE pls_gravar_consist_conf_sib ( nr_seq_lote_sib_p bigint, nr_seq_segurado_p bigint, cd_inconsistencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_inconsistencia_w		bigint	:= null;
ie_situacao_w			varchar(2)	:= null;
nr_seq_lote_inconsistencia_w	bigint;


BEGIN

begin
select	nr_sequencia,
	ie_situacao
into STRICT	nr_seq_inconsistencia_w,
	ie_situacao_w
from	SIB_INCONSISTENCIA_CONF
where	cd_inconsistencia	= cd_inconsistencia_p;
exception
when others then
	ie_situacao_w	:= 'I';
end;

if (ie_situacao_w = 'A') then

	select	nextval('pls_inconsist_conf_sib_seq')
	into STRICT	nr_seq_lote_inconsistencia_w
	;

	insert into PLS_INCONSIST_CONF_SIB(nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec,nm_usuario_nrec,
		nr_seq_lote_sib, nr_seq_segurado, nr_seq_inconsistencia)
	values (nr_seq_lote_inconsistencia_w, clock_timestamp(),nm_usuario_p, clock_timestamp(), nm_usuario_p,
		nr_seq_lote_sib_p, nr_seq_segurado_p, nr_seq_inconsistencia_w);
end if;

end;
$body$
