CREATE OR REPLACE PROCEDURE pls_gravar_digital_benef ( cd_pessoa_fisica_p text, ds_biometria_p text, ie_dedo_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_biometria_w		bigint;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	count(1)
	into STRICT	qt_biometria_w
	from	pessoa_fisica_biometria
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	ie_dedo			= ie_dedo_p;

	if (qt_biometria_w	= 0) then
		insert into pessoa_fisica_biometria(	nr_sequencia, cd_pessoa_fisica, ds_biometria, cd_empresa_biometria, dt_atualizacao,
							nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, ie_dedo)
						values (	nextval('pessoa_fisica_biometria_seq'), cd_pessoa_fisica_p, ds_biometria_p, 1, clock_timestamp(),
							nm_usuario_p, clock_timestamp(), nm_usuario_p, ie_dedo_p);
	elsif (qt_biometria_w	> 0) then
		update	pessoa_fisica_biometria
		set	ds_biometria	= ds_biometria_p,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	ie_dedo		= ie_dedo_p;
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
