CREATE OR REPLACE PROCEDURE pls_gravar_dlp_analise ( nr_seq_analise_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_w_dlp_w			bigint;
nr_seq_dlp_w			bigint;
ie_acao_preexistencia_w		varchar(10);
ie_classificacao_dlp_w		varchar(10);
ds_observacao_w			varchar(255);
qt_registros_w			bigint;
nr_seq_analise_dlp_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_dlp,
		ie_acao_preexistencia,
		ie_classificacao_dlp,
		ds_observacao
	from	w_pls_preexistencia
	where	nr_seq_analise	= nr_seq_analise_p
	and	ie_inserir	= 'S';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_w_dlp_w,
	nr_seq_dlp_w,
	ie_acao_preexistencia_w,
	ie_classificacao_dlp_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_analise_preexistencia
	where	nr_seq_analise	= nr_seq_analise_p
	and	nr_seq_preexistencia	= nr_seq_dlp_w;

	if (qt_registros_w = 0) then
		insert into pls_analise_preexistencia(	nr_sequencia, nr_seq_analise, cd_estabelecimento, nr_seq_preexistencia,
							dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
							ie_classificacao_dlp, ie_acao_sugerida, ds_observacao)
						values (	nextval('pls_analise_preexistencia_seq'), nr_seq_analise_p, cd_estabelecimento_p, nr_seq_dlp_w,
							clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
							ie_classificacao_dlp_w, ie_acao_preexistencia_w, ds_observacao_w);
	else
		select	max(nr_sequencia)
		into STRICT	nr_seq_analise_dlp_w
		from	pls_analise_preexistencia
		where	nr_seq_analise	= nr_seq_analise_p
		and	nr_seq_preexistencia	= nr_seq_dlp_w;

		update	pls_analise_preexistencia
		set	ie_classificacao_dlp	= ie_classificacao_dlp_w,
			ie_acao_sugerida	= ie_acao_preexistencia_w,
			ds_observacao		= ds_observacao_w
		where	nr_sequencia		= nr_seq_analise_dlp_w;

		delete from pls_analise_preexistencia
		where	nr_seq_analise	= nr_seq_analise_p
		and	nr_seq_preexistencia	= nr_seq_dlp_w
		and	nr_sequencia		<> nr_seq_analise_dlp_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
