CREATE OR REPLACE PROCEDURE pls_gravar_envio_email_relac ( ds_lista_destino_p text, nr_seq_grupo_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_lista_destino_w		varchar(2000);
nr_pos_virgula_destino_w	bigint;
ds_destino_w			varchar(2000);
cd_controle_w			smallint := 0;
nr_seq_envio_w			bigint;
ds_assunto_w			varchar(255)	:= '';


BEGIN
if (ds_lista_destino_p IS NOT NULL AND ds_lista_destino_p::text <> '') and (nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	ds_lista_destino_w := ds_lista_destino_p;

	while(ds_lista_destino_w IS NOT NULL AND ds_lista_destino_w::text <> '') and (cd_controle_w < 99) loop
		begin
		cd_controle_w			:= cd_controle_w + 1;

		nr_pos_virgula_destino_w	:= position(',' in ds_lista_destino_w);

		if (nr_pos_virgula_destino_w > 0) then
			begin
			ds_destino_w		:= substr(ds_lista_destino_w, 1, nr_pos_virgula_destino_w-1);
			ds_lista_destino_w	:= substr(ds_lista_destino_w, nr_pos_virgula_destino_w+1, length(ds_lista_destino_w));
			end;
		else
			begin
			ds_destino_w		:= ds_lista_destino_w;
			ds_lista_destino_w	:= null;
			end;
		end if;

		if (ds_destino_w IS NOT NULL AND ds_destino_w::text <> '') then
			begin
			nr_seq_envio_w := pls_gravar_envio_email_grupo(
				nm_usuario_p, ds_assunto_w, ds_destino_w, nr_seq_grupo_p, ds_observacao_p, cd_estabelecimento_p, nr_seq_envio_w);
			end;
		end if;
		end;
	end loop;
	end;
end if;

commit;

end;
