SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gravar_glosa_item_conta ( nr_seq_motivo_glosa_p bigint, nr_seq_ocorrencia_p bigint, nr_seq_conta_p bigint, nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, ds_observacao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

				 
nr_seq_ocorrencia_benef_w	pls_ocorrencia_benef.nr_sequencia%type;
nr_seq_conta_w			pls_conta.nr_sequencia%type;
nr_seq_conta_proc_w		pls_conta_proc.nr_sequencia%type;
nr_seq_conta_mat_w		pls_conta_mat.nr_sequencia%type;
ie_tipo_item_w			integer;
nr_seq_motivo_glosa_w		pls_ocorrencia.nr_seq_motivo_glosa%type;


BEGIN 
if (coalesce(nr_seq_motivo_glosa_p,0) > 0) and 
	((coalesce(nr_seq_conta_p,0) > 0) or (coalesce(nr_seq_conta_proc_p,0) > 0) or (coalesce(nr_seq_conta_mat_p,0) > 0)) then 
	insert into pls_conta_glosa(nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		nr_seq_conta, 
		nr_seq_conta_proc, 
		nr_seq_conta_mat, 
		nr_seq_motivo_glosa, 
		ds_observacao, 
		ie_lib_manual, 
		ie_situacao, 
		ie_fechar_conta) 
	values (nextval('pls_conta_glosa_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		CASE WHEN nr_seq_conta_p=0 THEN  null  ELSE nr_seq_conta_p END , 
		CASE WHEN nr_seq_conta_proc_p=0 THEN  null  ELSE nr_seq_conta_proc_p END , 
		CASE WHEN nr_seq_conta_mat_p=0 THEN  null  ELSE nr_seq_conta_mat_p END , 
		nr_seq_motivo_glosa_p, 
		ds_observacao_p, 
		'S', 
		'A', 
		'S');
end if;
 
--Inserir a ocorrência 
if (coalesce(nr_seq_ocorrencia_p,0) > 0) and 
	((coalesce(nr_seq_conta_p,0) > 0) or (coalesce(nr_seq_conta_proc_p,0) > 0) or (coalesce(nr_seq_conta_mat_p,0) > 0)) then 
	 
	--Busca o motivo de glosa da ocorrência, se tiver 
	select	max(nr_seq_motivo_glosa) 
	into STRICT	nr_seq_motivo_glosa_w 
	from	pls_ocorrencia 
	where	nr_sequencia	= nr_seq_ocorrencia_p;	
	 
	if (coalesce(nr_seq_conta_p,0) > 0) then 
		nr_seq_conta_w	:= nr_seq_conta_p;
		ie_tipo_item_w	:= 8;
	end if;
	 
	if (coalesce(nr_seq_conta_proc_p,0) > 0) then 
		nr_seq_conta_proc_w	:= nr_seq_conta_proc_p;
		ie_tipo_item_w 		:= 3;
		 
		--Busca a conta do procedimento 
		select	max(nr_seq_conta) 
		into STRICT	nr_seq_conta_w 
		from	pls_conta_proc 
		where	nr_sequencia	= nr_seq_conta_proc_w;
	end if;
	 
	if (coalesce(nr_seq_conta_mat_p,0) > 0) then 
		nr_seq_conta_mat_w	:= nr_seq_conta_mat_p;
		ie_tipo_item_w 		:= 4;
		 
		--Busca a conta do material 
		select	max(nr_seq_conta) 
		into STRICT	nr_seq_conta_w 
		from	pls_conta_mat 
		where	nr_sequencia	= nr_seq_conta_mat_w;
	end if;
	 
	nr_seq_ocorrencia_benef_w := pls_inserir_ocorrencia(	null, nr_seq_ocorrencia_p, null, null, nr_seq_conta_w, nr_seq_conta_proc_w, nr_seq_conta_mat_w, null, nm_usuario_p, ds_observacao_p, nr_seq_motivo_glosa_w, ie_tipo_item_w, cd_estabelecimento_p, 'S', null, nr_seq_ocorrencia_benef_w, null, null, null, null);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
