CREATE OR REPLACE PROCEDURE pls_gravar_glosa_item_serv_mat ( ds_lista_sequencias_p text, cd_motivo_p bigint, nr_seq_conta_p bigint, ds_observacao_p text, ie_servicos_materiais_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_lista_sequencias_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_seq_sequencia_w	bigint;

/* 
ie_servicos_materiais_p 
S = Servi√ßos 
M = Materiais 
*/
 
 

BEGIN 
if (ds_lista_sequencias_p IS NOT NULL AND ds_lista_sequencias_p::text <> '') and (cd_motivo_p IS NOT NULL AND cd_motivo_p::text <> '') and (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') and (ie_servicos_materiais_p IS NOT NULL AND ie_servicos_materiais_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
		 
	ds_lista_sequencias_w	:= ds_lista_sequencias_p;
		 
	while(ds_lista_sequencias_w IS NOT NULL AND ds_lista_sequencias_w::text <> '') loop 
		begin 
		 
		nr_pos_virgula_w	:= position(',' in ds_lista_sequencias_w);
	 
		if (nr_pos_virgula_w > 0) then 
			begin 
			nr_seq_sequencia_w	:= (substr(ds_lista_sequencias_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_sequencias_w	:= substr(ds_lista_sequencias_w,nr_pos_virgula_w+1,length(ds_lista_sequencias_w));			
			end;
		else 
			begin 
			nr_seq_sequencia_w	:= (ds_lista_sequencias_w)::numeric;
			ds_lista_sequencias_w	:= null;
			end;
		end if;
		 
		if (nr_seq_sequencia_w > 0) then 
			begin 
			 
			if (ie_servicos_materiais_p = 'S') then 
				begin 
				CALL pls_gravar_glosa_item_conta( 
					cd_motivo_p, 
					null, 
					0, 
					nr_seq_sequencia_w, 
					0, 
					ds_observacao_p, 
					nm_usuario_p, 
					cd_estabelecimento_p);
				end;
			elsif (ie_servicos_materiais_p = 'M') then 
				begin 
				CALL pls_gravar_glosa_item_conta( 
					cd_motivo_p, 
					null, 
					0, 
					0, 
					nr_seq_sequencia_w, 
					ds_observacao_p, 
					nm_usuario_p, 
					cd_estabelecimento_p);
				end;
			end if;
			end;
		end if;
		end;
	end loop;		
	end;
end if;
commit;
end;
$body$
