CREATE OR REPLACE PROCEDURE pls_gravar_hist_guia_req ( nr_seq_requisicao_p bigint, nr_seq_guia_p bigint, nr_seq_execucao_p bigint, ie_tipo_historico_p text, ds_historico_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_requisicao_w	pls_requisicao.nr_sequencia%type;


BEGIN
	if (nr_seq_execucao_p IS NOT NULL AND nr_seq_execucao_p::text <> '') then
		select	max(nr_seq_requisicao)
		into STRICT	nr_seq_requisicao_w
		from	pls_execucao_requisicao
		where	nr_sequencia = nr_seq_execucao_p;

		CALL pls_requisicao_gravar_hist(nr_seq_requisicao_w, ie_tipo_historico_p, ds_historico_p, nr_seq_execucao_p, nm_usuario_p);
	elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
		CALL pls_requisicao_gravar_hist(nr_seq_requisicao_p, ie_tipo_historico_p, ds_historico_p, null, nm_usuario_p);
	elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
		insert into pls_guia_plano_historico(nr_sequencia, nr_seq_guia, ie_tipo_log,
			dt_historico, dt_atualizacao, nm_usuario,
			ds_observacao, ie_origem_historico, ie_tipo_historico)
		values (	nextval('pls_guia_plano_historico_seq'), nr_seq_guia_p, '2',
			clock_timestamp(), clock_timestamp(), nm_usuario_p,
			ds_historico_p,'A','M');
	end if;

commit;

end;
$body$
